INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (20,'Dornsife','AMST','252mgw',4,'Black Social Movements in the U.S.','This course examines black social movements for freedom, justice, equality, and self determination. Beginning with Reconstruction, movements include labor, civil rights, radical feminism, socialism, reparations, Black Nationalism, prisoners\' rights, and Hip Hop.',1);
