INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (56,'Dornsife','ANTH','314g',4,'The Nature of Maya Civilization','A seminar forum on Maya culture from the earliest form to thepresent; problems of origins, classic florescence, systemscollapse, conquests, persistance, and transformation today.',1);
