INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (59,'Dornsife','ANTH','370',4,'Sex, Love, and Marriage: An Introduction to Kinship','Comparative examination of family and kinship in tribal, peasant, and complex societies, emphasizing non-Western cultures, societal and normative consequences of forms and functions in family.',1);
