INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (111,'Dornsife','ASTR','450',4,'Stellar Astrophysics','Observation and theory of stellar atmospheres and stellar interiors. Theory of stellar evolution. Physical and astronomical significance of the end states of stellar evolution. Prerequisite: PHYS-153L or PHYS-163L.',1);
