INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (119,'Dornsife','BISC','120Lg',4,'General Biology: Organismal Biology and Evolution','In-depth survey of key topics related to advances in our knowledge of the diversity of life and evolution; origin of life; eukaryotes/prokaryotes; ecology.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (120,'Dornsife','BISC','121Lg',4,'Advanced General Biology: Organismal Biology and Evolution','Equivalent to 120Lg, but taught at a higher level for exceptionally well-prepared students. Admission to the course by departmental approval only.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (121,'Dornsife','BISC','150Lxg',4,'The Nature of Human Health and Disease','The human organism; the nature of inherited and acquired diseases;the biological and societal basis for the AIDS epidemic; therapy,drug design and the future. Not available for major credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (122,'Dornsife','BISC','193',1,'Introduction to Research I','A series of lectures and discussions at which faculty of the department introduce their research activities to students entering biology and related majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (123,'Dornsife','BISC','220Lg',4,'General Biology: Cell Biology and Physiology','In-depth survey of key topics related to advances in our knowledge of cellular biology and physiology; cell composition/metabolism; gene action; organism structure and function. <strong>Recommended preparation:</strong> high school chemistry; BISC 120L or BISC 121L.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (124,'Dornsife','BISC','230Lxg',4,'The Biology of the Brain','The structure and function of the mammalian brain including the role of the brain in regulating behavior, both in normal and diseased states; in relation to mind; and in comparison with machine forms of intelligence.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (125,'Dornsife','BISC','300L',4,'Introduction to Microbiology','Comparative approach to bacteria, Archaea and viruses; their structure, life cycles, geochemical activity, ecology and nutrition. Fundamentals of metabolism and microbial genetics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (126,'Dornsife','BISC','305',4,'Introduction to Statistics for Biologists','Statistical methods in biological science and medicine, including populations and samples, random sampling, confidence intervals, paired samples and regression.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (127,'Dornsife','BISC','315L',4,'Introduction to Ecology','Organism-environment interactions; dynamics of populations, communities, and ecosystems; evolutionary forces. Lecture, 3 hours; laboratory, 3 hours.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (128,'Dornsife','BISC','320Lg',4,'Molecular Biology','Structure and synthesis of nucleic acids and proteins; molecular biology of prokaryotes and eukaryotes; genetics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (129,'Dornsife','BISC','325',4,'Genetics','Transmission genetics and genotype/phenotype; mapping methods; complex traits; genetics of human disease and population genetics.',1);
