INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (131,'Dornsife','BISC','403',4,'Advanced Molecular Biology','Molecular mechanisms and control of DNA replication, DNA repair, recombination, gene expression, cell growth, and development in prokaryotic and eukaryotic organisms, from bacteria to humans. <strong>Recommended preparation:</strong> BISC 313 or BISC 325',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (132,'Dornsife','BISC','421',4,'Neurobiology','Structure, function, and development of nervous systems; neural integration and mechanisms of behavior; organization and operation of brains.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (133,'Dornsife','BISC','438',4,'Nutritional Biochemistry','Metabolism of carbohydrates, proteins, fats. Biochemical basis of nutritions impact on metabolic functions, including vitamins, minerals and biologically active non-nutrients; medical aspects of nutrition.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (134,'Dornsife','BISC','444',2,'Practical Analysis of Biological Data in R','Instruction in the open-source statistical program environment R to analyze biological data; manipulation of large datasets and customization of statistical tests using simulations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (135,'Dornsife','BISC','450L',4,'Principles of Immunology','Immune processes, humoral and cellular; immunoglobulins; antibody formation; antigen-antibody interactions; immune dyscrasias; transplantation and tumor immunology; basic hematology and immuno-hematology. Lecture, 3 hours; laboratory, 3 hours.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (136,'Dornsife','BISC','461',2,'Seminar in Molecular and Computational Biology','Topical seminar in molecular and computational biology. Junior, senior or graduate standing.',1);
