INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (138,'Dornsife','BISC','473L',4,'Biological Oceanography','Biological, physical, chemical dynamics and analysis of the ocean; primary production of phytoplankton, secondary production by zooplankton, bacterial remineralization; physiology, ecology of fishes, marine mammals.',1);
