INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (140,'Dornsife','BISC','486',4,'Regenerative Medicine: Principles, Paradigms and Practice','Examination of molecular and cellular processes that generate, maintain and repair body systems. Special emphasis on stem cell biology with clinical applications to regenerative medicine.',1);
