INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (165,'Dornsife','CHEM','050x',2,'General Chemistry Tutorial','Weekly tutorial for selected students in CHEM 105aL. Strong emphasis on chemical mathematics and key concepts in general chemistry. Topics parallel lectures in CHEM 105aL. Not available for degree credit. Graded CR/NC. Discussion, 2 hours.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (166,'Dornsife','CHEM','102',4,'The Molecular World','Basic chemistry and its impact on the contemporary world. Topics include: structures and reactions of molecules, stoichiometry, nomenclature, gases, solutions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (167,'Dornsife','CHEM','103Lxg',4,'General Chemistry for the Environment and Life','Chemistry for environmental studies, neuroscience and other life sciences: organic and inorganic structures, nomenclature, stoichiometry, solutions, gases, non-covalent interactions, equilibria, acid-base and redox reactions. Not for major credit in chemistry.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (168,'Dornsife','CHEM','105aLg',4,'General Chemistry','Fundamental principles and laws of chemistry; laboratory work emphasizes quantitative procedures. Prerequisite to all more advanced courses in chemistry. Lecture, 3 hours; laboratory and discussion, 4 hours. Quiz, 1 hour.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (169,'Dornsife','CHEM','105bL',4,'General Chemistry','Fundamental principles and laws of chemistry; laboratory work emphasizes quantitative procedures. Prerequisite to all more advanced courses in chemistry. Lecture, 3 hours; laboratory and discussion, 4 hours. Quiz, 1 hour. Prerequisite: one year of high school chemistry; CHEM-105aLg or 115aLg.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (170,'Dornsife','CHEM','107Lg',4,'General Chemistry for Chemistry Majors','Fundamental principles and laws of chemistry emphasizing contemporary chemistry; laboratory work focuses on quantitative procedures. Open only to Chemistry majors. (Duplicates credit in CHEM 105a and CHEM 115a)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (171,'Dornsife','CHEM','115aLg',4,'Advanced General Chemistry','Equivalent to 105agL-bL, but taught at a higher level forexceptionally well-prepared students. Admission to course bydepartmental approval only. Lecture, 3 hours; lab and discussion,4 hours; quiz, 1 hour. Prereq: 1 year high school chemistry andmathematics through pre-calculus.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (172,'Dornsife','CHEM','203Lxg',4,'AIDS Drug Discovery and Development','Scientific principles underlying molecular approaches to diagnosis and treatment of diseases, using specific models within a societal (business, legal, ethical) context. Not available for major credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (173,'Dornsife','CHEM','250x',2,'Organic Chemistry Tutorial A','Major principles of organic chemistry and its relevance. Topics covered parallel to those of CHEM 322A. Prerequisite: CHEM 105bL. Corequisite: CHEM 322aL. Graded CR/NC. Not available for degree credit.',1);
