INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (180,'Dornsife','CHEM','300L',4,'Analytical Chemistry','Theory and practice in chemical analysis, emphasizing instrumental techniques; error analysis, fractional distillation, extraction; chromatograhpy; visible, ultraviolet, and infrared spectroscopy; introductions to electrochemistry and nuclear magnetic resonance spectroscopy. Lecture, 2 hours; Laboratory and Discussion.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (181,'Dornsife','CHEM','322aL',4,'Organic Chemistry','Chemistry of the carbon compounds of the aliphatic and aromatic series; laboratory preparation of typical compounds of both series. Lecture, jointly with 325aL and 325bL, 3 hours; laboratory and discussion, 4 hours. For premedical and predental students, and some categories of biology majors and engineers.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (182,'Dornsife','CHEM','322bL',4,'Organic Chemistry','Chemistry of the carbon compounds of the aliphatic and aromatic series; laboratory preparation of typical compounds of both series. Lecture, 3 hours jointly with CHEM 325abL; laboratory and discussion, 4 hours. For premedical and predental students, and some categories of biology majors and engineers.',1);
