INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (186,'Dornsife','CHEM','426',4,'Advanced Organic Chemistry','Advanced treatment of organic chemistry from a mechanistic point of view according to the following topics: polar and isopolar reactions, intermediates. Lecture, 3 hours; discussion, 1 hour.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (187,'Dornsife','CHEM','430',4,'Physical Chemistry: Thermodynamics and Kinetics','Kinetic theory; equations of state; thermodynamics; phase equilibria; chemical equilibrium; nuclear chemistry, wave mechanics; spectroscopy; statistical thermodynamics; kinetics; electrochemistry; surface and colloid chemistry.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (188,'Dornsife','CHEM','467',2,'Advanced Chemical Biology Laboratory','Principles, methods and protocols of chemical biology through experimentation focusing on the interactions of small molecules with biomacromolecules such as proteins and DNA.',1);
