INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (191,'Dornsife','CHEM','515',4,'Structure and Bonding in Inorganic and Organometallic Chemis','An integrated core course of structure and bonding in inorganic, coordination, and organometallic chemistry within an oxidation state framework; symmetry, electronic properties.',1);
