INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (340,'Dornsife','ECON','203g',4,'Principles of Microeconomics','Behavior of firms and consumers, functions of the price system, competition and monopoly, labor markets, poverty, government regulation, international trade, and the environment.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (341,'Dornsife','ECON','205g',4,'Principles of Macroeconomics','Unemployment, inflation and output determination and links.Effects of government taxation and spending on growth, investment,saving, consumption, and trade.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (342,'Dornsife','ECON','303',4,'Intermediate Microeconomic Theory','Decision making by business firms, consumer preferences and behavior, uncertainty, competition, monopoly, labor and resource markets, efficient resource allocation, externalities, and government policy.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (343,'Dornsife','ECON','305',4,'Intermediate Macroeconomic Theory','The determinants of aggregate income, employment, and inflation;economic fluctuations; fiscal and monetary policy; financialmarkets; the national debt.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (344,'Dornsife','ECON','317',4,'Introduction to Statistics for Economists','Introduction to statistical methods appropriate for analyzing economic data: probability theory, random variables and probability distributions, sampling, estimation, statistical inference.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (345,'Dornsife','ECON','318',4,'Introduction to Econometrics','Application of statistical methods to economic data: estimating economic relationships using regression analysis, testing hypotheses involving economic behavior, forecasting economic variables.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (346,'Dornsife','ECON','342',4,'Economic Development of the Middle East','Contemporary economic problems of the Middle East: comparative and historical perspectives on issues of institutions, investment, oil, trade, migration, finance, inequality, labor and capital markets. <strong>Recommended preparation:</strong> ECON 205 and ECON 303.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (347,'Dornsife','ECON','344',4,'Economic Development of Sub-Saharan Africa','Contemporary economic problems of sub-Saharan economies: policies and endowments. Focus on issues of poverty, agriculture, health, macroeconomy and political economy.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (348,'Dornsife','ECON','351x',4,'Microeconomics for Business','Development and business applications of: theory of the firm; theory of the consumer; inter-temporaldecisions; decisions under risk; market failures; industrial and enterprise structure. Prerequisites: MATH 118 or MATH 125 or MATH 126 or MATH 226. Not for major credit for: Economics, Economics/Mathematics, Social Sciences (Economics). Duplicates credit in former ECON 251.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (349,'Dornsife','ECON','352x',4,'Macroeconomics for Business','Theoretical development and significance to business and markets of economic growth; inflation;unemployment; monetary and fiscal policy; business cycles; savings and investment; exchange rates. Corequisite: ECON 351. <strong>Recommended preparation:</strong> introductory economics course, high school math, and algebra. Not for major credit for: Economics, Economics/Mathematics, Social Sciences (Economics). Duplicates credit in former ECON 252x.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (350,'Dornsife','ECON','357',4,'Money, Credit, and Banking','The money, bond, stock, and other financial markets; portfolio choice; determinants of asset prices and interest rates; inflation; interactions between financial markets and government policies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (351,'Dornsife','ECON','361',4,'Understanding Financial Crises','Key facts and basic mechanisms concerning financial crises and related topics (bank runs, sovereign default decision, currency collapse).',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (352,'Dornsife','ECON','395',4,'Economic Policy Issues','Selected policy dilemmas, including welfare reform, urban renewal, government budget deficits, regulation and deregulation, environmental problems, immigration, and global development. Lectures by leading authorities and weekly discussion sessions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (353,'Dornsife','ECON','401',4,'Mathematical Methods in Economics','Introduction to quantitative methods for analyzing economic equilibria; comparative statics and dynamics. Utility theory, consumer behavior, and profit maximization. Model formulation in micro and macroeconomics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (354,'Dornsife','ECON','404',4,'Games and Economics','Analysis of strategic economic interactions. Topics include bargaining, insurance, patents, voting, environment depletion, strategic trade, learning, reputation, strikes, corporate takeovers, and the provision of public goods.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (355,'Dornsife','ECON','419',4,'Advanced Econometrics','Analysis of binary dependent variable models, panel data analysis, program evaluations, IV analysis, basics of time series and forecasting.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (356,'Dornsife','ECON','420',4,'Experimental Economics','Examination of economic theories and patterns of behavior useful in building new theories. Prerequisite ECON-303; <strong>Recommended preparation:</strong> ECON-317.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (357,'Dornsife','ECON','432',4,'Economics of Happiness','What is happiness? How does it vary by socio-economic status and over the life cycle? This course will develop insight into the nature and determinants of subjective well-being. <strong>Recommended preparation:</strong> ECON 305',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (358,'Dornsife','ECON','450',4,'International Trade','Determinants and economic consequences of international trade patterns; effects of trade restrictions and trading blocs; trade negotiations and arrangements.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (359,'Dornsife','ECON','457',4,'Financial Markets','General equilibrium analysis of economics with financial markets; decision making under uncertainty; methods of risk reduction; portfolio theory and valuation of securities; efficiency of security markets.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (360,'Dornsife','ECON','471',4,'Economics of Labor Markets and Human Capital','A human capital interpretation of labor demand and supply; wage determination, differentials, and discrimination; job turnover and occupational mobility; unions and collective bargaining.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (361,'Dornsife','ECON','472',4,'Economics of Medical Care','Health as an investment in human capital; analysis of the demand for and supply of health services and manpower; health insurance; cost-effectiveness analysis; market structures and the pricing of medical services.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (362,'Dornsife','ECON','474',4,'Economic Consulting and Applied Managerial Economics','Economic consulting and managerial economic methods applied in real world problems. Economic methods to analyze issues of intellectual property, environmental damage, trademark infringement, brand value, and consumer demand. Prerequisite: ECON 303.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (363,'Dornsife','ECON','490x',1,'Directed Research','Supervised individual research. Not available for graduate credit. Requires departmental approval.',1);
