INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (365,'Dornsife','ECON','500',4,'Microeconomic Analysis and Policy','Theories of the household and the firm; product and factor markets; perfect and imperfect competition; welfare criteria.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (366,'Dornsife','ECON','501',4,'Macroeconomic Analysis and Policy','Theories of aggregate economic activity; design and use of macroeconometric models; stabilization and control of inflation, unemployment, and growth.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (367,'Dornsife','ECON','513',4,'Practice of Econometrics','Application of econometric tools using standard econometric software packages for microcomputers; empirical applications to selected economic problems of estimation and inference. Prerequisite: ECON 318.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (368,'Dornsife','ECON','515',4,'Time Series Analysis','Understanding and implementing models commonly used in time series econometrics. Emphasis is placed on intuition and application. Assists students understanding how to use time series data to test hypotheses and serve as an introduction to the ideas and techniques of forecasting.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (369,'Dornsife','ECON','521',4,'Open Economy Macroeconomics','The balance of payments, macroeconomic policy in an open economy, exchange rate determination, exchange rate systems, currency crises, international financial arrangements and monetary history. Prerequisite: ECON 501.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (370,'Dornsife','ECON','527',4,'Theory of Value: Classical Origins and Neoclassical Critique','Classical economic theory; its precursors, main contributors, extensions, and critics; focus upon the writings and ideas of Smith, Say, Malthus, Ricardo, Mill and Marx. Prerequisite: ECON 303 and ECON 305.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (371,'Dornsife','ECON','538',4,'Values and Social Analysis','Factors that make values an essential feature of human society; how values develop, change, and are abandoned; role of values in social order and economic development.',1);
