INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (379,'Dornsife','ECON','601',4,'Microeconomic Theory I','Optimization of the consumer and the firm; duality and imputed value; perfect and imperfect competition in product and factor markets. Prerequisites: ECON 401. <strong>Recommended preparation:</strong> ECON 500. Duplicates credit in former ECON 503.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (380,'Dornsife','ECON','602',4,'Macroeconomic Theory I','Aggregate demand, supply and government policy; theories of economic growth and business cycles; static and dynamic implications of government policies. Prerequisite: ECON 401. Duplicates credit in former ECON 505.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (381,'Dornsife','ECON','611',4,'Probability and Statistics for Economists','Introduction to probability theory and statistical inference to prepare students for graduate courses in econometrics and economic theory; probability, random variables, distributions, estimation, testing, asymptotics. Prerequisite: MATH-226. Duplicates credit in former ECON-514.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (382,'Dornsife','ECON','613',4,'Economic and Financial Time Series I','Simultaneous equation models, dynamic structural econometric models, vector autoregressions, causality, forecasting, univariate and multivariate nonstationary time series, tests for unit roots, cointegration, autoregressive conditional heteroscedasticity models, time series models with changes in regime. Prerequisite: ECON 609.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (383,'Dornsife','ECON','615',4,'Applied Econometrics','Use of quantitative models to describe and forecast economic activity; estimation and application of such models to selected policy problems. Prerequisites: ECON 609.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (384,'Dornsife','ECON','616',4,'Experimental Economics','Laboratory methods for testing economic theory; experimental comparison of alternative market and non-market institutions; identification of behavioral responses to alternative regulations. Prerequisite: ECON-500 or ECON-601.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (385,'Dornsife','ECON','620aL',2,'Experimental Methods','Experimental methods of and design of computer-based experiments. Use of standard software for data collection in individual decision-making experiments and games. Prerequisite: ECON-601. <strong>Recommended preparation:</strong> ECON-616. Graded C/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (386,'Dornsife','ECON','634',4,'Political Economy of Institutions','The functions of laws, rules, customs, conventions, and other restrictions on economic and social activity. Theories of institutional evolution. Prerequisites: ECON-500 or ECON-601.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (387,'Dornsife','ECON','642',4,'Poverty, Human Resources and Economic Development','Household production models and intra-household models of behavior and their empirical implementation, focus on poverty, human resource investments and their interaction with public policies. Prerequisites: ECON 501 and ECON 609.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (388,'Dornsife','ECON','652',4,'Economics of Financial Markets II','Financial market equilibrium and partial equilibrium asset pricing in discrete and continuous time; properties of equilibria with and without complete markets: theory of option prices: Black-Scholes pricing formula; term structure of interest rates; hedging strategies and managing market risk using options, futures and swaps; hedging exchange-rates risks. Prerequisite: ECON-601.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (389,'Dornsife','ECON','663',4,'Financial and Monetary Macroeconomics','Macroeconomic models of financing and nominal rigidities. Focuses on the ways through which the financial system or financing constraints can affect macroeconomic fluctuations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (390,'Dornsife','ECON','680',4,'Industrial Organization','Decision making, economic behavior and organization in firms; types of competition and market structure; property rights, nonprofit decision making. Prerequisite: ECON-500 or ECON-601.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (391,'Dornsife','ECON','681',4,'Economics of Regulated Industries','Theories and methods of government regulation; effects of regulation on various industries; behavior of regulatory agencies. Prerequisites: ECON-500 or ECON-601.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (392,'Dornsife','ECON','687',4,'Urban Economics','Economic models will be used to understand the main facts observed in cities in the developed and developing world. Special attention will be paid to empirical hypothesis testing and developing an urban economics research agenda. Students will be exposed to new data sets being collected using Smart Phones and other novel technologies that have greatly expanded our ability to test relevant hypotheses.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (393,'Dornsife','ECON','690',2,'Seminar in Economic Theory','Current research in economic theory presented by faculty, students, and outside scholars. Graded CR/NC.',1);
