INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (415,'Dornsife','ENGL','351',4,'Periods and Genres in American Literature','A concentrated reading and criticism of the works of one period or one genre of American literature; for example, colonial literature, the American Renaissance, American poetry, American drama.',1);
