INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (417,'Dornsife','ENGL','361g',4,'Contemporary Prose','Study of prose written in English since 1945, principally fiction of the past two decades.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (418,'Dornsife','ENGL','363g',4,'Contemporary Drama','Selected British, Irish, and American drama from the post World War II period (1945 to the present).',1);
