INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (425,'Dornsife','ENGL','423',4,'English Literature of the 18th Century (1660-1780)','Selected studies in the poetry, prose, and fiction of such writers as Defoe, Dryden, Fielding, Richardson, Pope, Swift, and Johnson.',1);
