INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (427,'Dornsife','ENGL','440',4,'American Literature to 1865','American poetry and prose to the Civil War with special attention to Irving, Cooper, Poe, Hawthorne, Emerson, Thoreau, Melville, and Whitman.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (428,'Dornsife','ENGL','442',4,'American Literature, 1920 to the Present','American poetry, fiction, and drama since World War I with special attention to Eliot, Frost, Hemingway, Fitzgerald, O\'Neill, Stevens, Faulkner, and Nabokov.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (429,'Dornsife','ENGL','444m',4,'Native American Literature','Survey of Native American literature, including oral traditions and print genres, such as short story, poetry, novel, and autobiography, from 1700 to the present. <strong>Recommended preparation:</strong> ENGL 263.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (430,'Dornsife','ENGL','449m',4,'Asian American Literature','Survey of Asian American literature from the earliest time to the present; development of prose, poetry, and novel.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (431,'Dornsife','ENGL','466',4,'The 19th Century English Novel','Theory and practice of fiction in works of major writers such as Austen, Dickens, Thackeray, George Eliot, Meredith, and Hardy.',1);
