INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (458,'Dornsife','ENST','100g',4,'Introduction to Environmental Studies','Gateway to the majors and minors in environmental studies. Provides students with an overview of how government agencies and societal institutions address (or fail to address) the interrelated social and scientific aspects of environmental problems and policies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (459,'Dornsife','ENST','150xg',4,'Environmental Issues in Society','Exploration of the major social, political, economic, religious, and philosophical disagreements that exist between scholars, leaders, and citizens concerning today\'s most serious environmental issues and problems. Not available for credit to environmental studies majors and minors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (460,'Dornsife','ENST','320a',4,'Water and Soil Sustainability: Energy and Air Sustainability','Overview of issues related to water and soil sustainability including science, policy and business aspects. Recommended Preparation ENST-100.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (461,'Dornsife','ENST','320b',4,'Water and Soil Sustainability: Energy and Air Sustainability','Overview of issues related to water and soil sustainability including science, policy and business aspects. Recommended Preparation ENST-100.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (462,'Dornsife','ENST','323',4,'Politics of Global Environment','Examines the politics of managing the global environment. The nature of ecosystems, commons problems, population and resource utilization problems along with biodiversity and global governance are emphasized.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (463,'Dornsife','ENST','344',4,'Environmental Ethics','Explores issues concerning behavior of humans towards the nonhuman world. Examines a wide spectrum of ethical issues.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (464,'Dornsife','ENST','387',4,'Economics for Natural Resources and the Environment','An introduction to the economic tools and issues that affect natural resource use and environmental management.',1);
