INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (467,'Dornsife','ENST','492',2,'Directed Environmental Policy and Science Internship','Provides opportunities for professional development in environmental policy and science and prepares students for career or graduate school in environmental-related fields. Students are placed in internships and guided before and during the internship period. Student gain practical experience and learn how government and nongovernmental organizations pursue and implement environmental policy and science work.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (468,'Dornsife','ENST','495',4,'Senior Seminar in Environmental Studies','Students form multidisciplinary teams and are asked to study and resolve a major environmental problem facing a particular region or target population.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (469,'Dornsife','ENST','500',4,'Interdisciplinary Approaches to Environmental Studies','Students will develop advanced skills to address complex environmental issues that society faces today. Different approaches for studying environmental processes and resources will be examined.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (470,'Dornsife','ENST','540',4,'California Coastal Zone Science and Policy','Science and policy issues used to characterize and manage California coastal resources. Key issues include: coastal pollution, public health, ecosystem management, and marine reserves. <strong>Recommended preparation:</strong> ENST-500.',1);
