INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (494,'Dornsife','FSEM','100',2,'Freshman Seminar','A seven-to-eleven week course offered for incoming freshmen; limited to 18 students. Graded CR/NC. A combined maximum of 4 units of FSEM 100 and FSEM 101 may be applied to the degree.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (495,'Dornsife','GEOL','105Lg',4,'Planet Earth','Geologic structure and evolution of planet earth. Principles ofplate tectonics, rocks and minerals, processes of mountainbuilding, continent and ocean formation, earthquakes, volcanism,development of landforms by running water and glaciers. Lecture, 3hours; laboratory, 2 hours. One all-day or two-day field triprequired.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (496,'Dornsife','GEOL','107Lg',4,'Oceanography','Physical, chemical, and geological character of the oceans andocean basins. Origin of the oceans. Ocean processes and agents.Economic value of the oceans. Lecture, 3 hours; laboratory, 2 hours. One all-day field trip required.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (497,'Dornsife','GEOL','108Lg',4,'Crises of a Planet','Impact of civilization on planet earth, and impact of earth\'s natural evolution on society: earthquakes, volcanism, landslides, floods, global warming, acid rain, groundwater depletion and pollution; mineral and fossil fuel depletion, formation of the ozone hole. Lecture, 3 hours; laboratory, 2 hours. One all-day or overnight field trip.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (498,'Dornsife','GEOL','150Lg',4,'Climate Change','Climate systems from the beginning of earth history to the present; tools and techniques used to reconstruct prehistoric climate records; effects of climate variations on development of life forms on earth.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (499,'Dornsife','GEOL','160Lg',4,'Introduction to Geosystems','Survey of natural geological/ environmental processes (systems) and variability active near the earth\'s surface in the region that houses most life (the biosphere).',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (500,'Dornsife','GEOL','240Lg',4,'Earthquakes','Causes of earthquakes and nature of large faults; earthquake hazard and risk; world\'s great earthquakes; understanding the Richter scale. Lecture, 3 hours; laboratory, 2 hours; one fieldtrip required.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (501,'Dornsife','GEOL','290L',1,'Special Laboratory','Laboratory component for GEOL 105Lg, 107Lxg, 108Lg, 125Lxg, 130Lxg, 150Lxg, or 240Lxg for students with equivalent lecture credit from another institution.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (502,'Dornsife','GEOL','315L',4,'Minerals and Earth Systems','Minerals and their formation in Earth geosystems; includes discussions of mineral properties, crystal structures, uses and biogeochemical importance. Lecture, 3 hours; laboratory, 6 hours; required field trips. <strong>Recommended preparation:</strong> any introductory GEOL course.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (503,'Dornsife','GEOL','432L',2,'Introduction to Field Techniques','Introduction to field techniques used in a variety of Earth Science disciplines. Field and digital techniques for map making including use of GIS, GPS, and digital tablets. Field analyses of rocks and rock structures. Map interpretation and digital processing of field data. Includes weekly labs and some weekend field trips.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (504,'Dornsife','GEOL','433L',4,'Paleontology and Evolution in Deep Time','Origin and evolution of life; Precambrian life; evolutionary history of major groups during the Phanerozoic; mass extinctions; deep time and evolutionary processes. <strong>Recommended preparation:</strong> any introductory GEOL course. Lecture, 3 hours; laboratory, 3 hours; required field trips.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (505,'Dornsife','GEOL','450L',4,'Geosystems','Geosystems, such as mantle convection, active faults, climate, and the carbon cycle, will be studied using numerical models and concepts such as chaos, universality, emergence, and intermittency. Lecture, 3 hours; laboratory, 2 hours. <strong>Recommended preparation:</strong> MATH 126.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (506,'Dornsife','GEOL','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
