INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (508,'Dornsife','GEOL','499',2,'Special Topics','Special topics in the earth sciences. Field trip required when appropriate to the topic. Departmental approval required.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (509,'Dornsife','GEOL','500',3,'Marine Paleoecology','Principles of marine paleoecology; interrelationships between marine organisms and their environment in geologic time. <strong>Recommended preparation:</strong> GEOL-577L',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (510,'Dornsife','GEOL','505',2,'Introductory Graduate Seminar in Earth Sciences','Lectures by Earth Sciences faculty about current research; introduction of new graduate students to the breadth of current research; applying for research funding; practicing effective research presentations. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (511,'Dornsife','GEOL','512',4,'Introduction to Chemical and Physical Oceanography','Principles of physical, chemical, and geological oceanography including discussions of air-sea interaction, biogeochemical cycling and the role of the ocean in modulating climate and atmospheric composition; discussion section will cover formulation of basic calculations that illustrate these principles.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (512,'Dornsife','GEOL','515',3,'Introduction to Atmospheric Science','Elementary physical principles underlying the behavior of Earth\'s atmosphere. Dry and moist thermodynamics, radiative transfer, conservation laws, fundamental dynamical balances, instability theory, cloud physics. <strong>Recommended preparation:</strong> PHYS 161L, PHYS 304.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (513,'Dornsife','GEOL','530',3,'Modern Perspectives on Crustal Dynamics','Deformation mechanisms, strength and structure of the crust. Fractal scaling in structures and dynamic processes. Geodetic measurement of crustal deformation and spatio-temporal patterns of seismicity.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (514,'Dornsife','GEOL','532',3,'Advanced Geologic Mapping','Principles of mapping geologically complex terranes of different structural style. Fieldwork will be coordinated with seminar review of diverse structural phenomena. Field trips. <strong>Recommended preparation:</strong> 321L and 465.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (515,'Dornsife','GEOL','566',1,'Geochemistry Seminar','Current topics in geochemistry.',1);
