INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (640,'Dornsife','IR','312',4,'Introduction to Data Analysis','Introduction to statistical analysis with R, with applications to the study of International Relations: data manipulation, hypothesis testing, regression, causality, text analysis, visualization, and network analysis.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (641,'Dornsife','IR','323',4,'Politics of Global Environment','Examines the politics of managing the global environment. The nature of ecosystems, commons problems, population and resource utilization problems along with biodiversity and global governance are emphasized.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (642,'Dornsife','IR','324',4,'Multinational Enterprises and World Politics','Political implications of interactions between different types of multinational enterprises and all levels of U.S. government, other industrial nations, and less-developed countries.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (643,'Dornsife','IR','325',4,'North-South Relations in the Global Economy','Analysis of inequality in relations between developed and developing states; contending views on the causes and legacies of imperialism; development strategies and aid.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (644,'Dornsife','IR','328',4,'The Evolving Global Economic Architecture-Capital Mobility','Exploration of the evolving global economic architecture of the past seventy years; overview of the possible future contours of economic and political globalization. <strong>Recommended preparation:</strong> Macro-and Micro-Economics',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (645,'Dornsife','IR','330',4,'Politics of the World Economy','Exploration of questions and concepts in international economics and politics, especially as they relate to flows of trade, capital, people, and problems across borders.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (646,'Dornsife','IR','331',4,'The Global Economy 2030','Examination of key ideas fromeconomics, demography and technology. Guest lecturersilluminate possible conditions of the Global Economy in 2030.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (647,'Dornsife','IR','333',4,'China in International Affairs','Economic reform, the open door, and China\'s changing role in the international system. Relations with the United States, Japan, and other key powers in Asia. Tensions between the interests of American business and the human rights community over China policy.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (648,'Dornsife','IR','339',4,'Public Health and International Relations','Examination of threats to global security, state sovereignty and population health posed by infectious diseases, natural disasters, war and civil conflict.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (649,'Dornsife','IR','341',4,'Foreign Policy Analysis','Basic concepts and analytical approaches in the study of decision-making at the international level. This is a case-based class, requiring participation of students in interactive discussions of decision forcing and retrospective foreign policy cases.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (650,'Dornsife','IR','342',4,'The Politics and Strategy of Weapons of Mass Destruction','Causes and effects of use and spread of nuclear, biological and chemical weapons; responses to WMD, including deterrence, preventive war, and non-proliferation policies and negotiations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (651,'Dornsife','IR','346',4,'Communism and Post-Communism: Eastern Europe and the Balkans','Analysis of communism and post-communism in East-Central Europe, including economic transitions, nationalism, inter- and intra-state conflict, NATO and EU enlargement.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (652,'Dornsife','IR','350p',4,'Power and Authority in the Middle East','A comparative examination of social relations of power and institutions with a special focus on intellectual traditions, the law, and social movements.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (653,'Dornsife','IR','360',4,'International Relations of the Pacific Rim','Political, economic and security relations among the countries in East Asia and the Pacific with the emphasis on the role of the United States, China and Japan.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (654,'Dornsife','IR','363',4,'Middle East Political Economy','Examination of general economic development issues: population, agriculture, industrialization, trade, oil, etc. Several Mideast case study countries are then explored in depth.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (655,'Dornsife','IR','364',4,'The Political Economy of Latin American Development','The main economic development themes and strategies in Latin America over the past century. The interplay between domestic and international variables, and the resulting dynamic changes.',1);
