INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (735,'Dornsife','MATH','117g',4,'Introduction to Mathematics for Business and Economics','Functions, graphs, polynomial and rational functions, exponential and logarithmic functions, matrices, systems of linear equations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (736,'Dornsife','MATH','118xg',4,'Fundamental Principles of Calculus','Derivatives; extrema. Definite integral; fundamental theorem of calculus. Extrema and definite integrals for functions of several variables. Not available for credit toward a degree in mathematics. Prerequisites: MATH 108 or MATH 117 or placement exam in MATH.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (737,'Dornsife','MATH','125g',4,'Calculus I','Limits; continuity, derivatives and applications; antiderivatives; the fundamental theorem of calculus; exponential and logarithmic functions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (738,'Dornsife','MATH','126g',4,'Calculus II','A continuation of MATH 125: trigonometric functions; applications of integration; techniques of integration; indeterminate forms; infinite series; Taylor series; polar coordinates.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (739,'Dornsife','MATH','127',4,'Enhanced Calculus II','Applications of integration, review of techniques of integration, infinite sequences and series, some beginning linear algebra, ordinary differential equations. Designed for students who earn a score of 4 or 5 on the Advanced Placement Calculus AB Examination, or a score of 3 or 4 on the BC Examination. Admission to course by departmental approval. Duplicates credit in MATH 126.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (740,'Dornsife','MATH','129',4,'Calculus II for Engineers and Scientists','Trigonometric functions; applications of integration; techniques of integration; indeterminate forms; infinite series; Taylor series; polar coordinates. Engineering and physics applications. Prerequisite: MATH 125. (Duplicates credit in MATH 126 and MATH 127)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (741,'Dornsife','MATH','208x',4,'Elementary Probability and Statistics','Descriptive statistics, probability, discrete and continuous random variables, expectation and variance, sampling, Central Limit Theorem, estimation, hypothesis testing, correlation and regression. Emphasis on health science. Prerequisite: MATH 118xg or MATH 125.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (742,'Dornsife','MATH','225',4,'Linear Algebra and Linear Differential Equations','Matrices, systems of linear equations, vector spaces, linear transformations, eigenvalues, systems of linear differential equations. Prerequisites: MATH 126 or MATH 127.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (743,'Dornsife','MATH','226g',4,'Calculus III','A continuation of MATH 126; vectors, vector valued functions; differential and integral calculus of functions of several variables; Green\'s theorem.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (744,'Dornsife','MATH','229',4,'Calculus III for Engineers and Scientists','A continuation of 129; vectors, vector valued functions; differential and integral calculus of functions of several variables; Greens theorem. Engineering and physics applications. Prerequisite: MATH 126, or MATH 127, or MATH 129.(Duplicates credit in MATH 226 and MATH 227)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (745,'Dornsife','MATH','245',4,'Mathematics of Physics and Engineering I','First-order differential equations; second-order linear differential equations; determinants and matrices; systems of linear differential equations; Laplace transforms. Prerequisites: MATH 226 or MATH 227.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (746,'Dornsife','MATH','307',4,'Statistical Inference and Data Analysis I','Probability, counting, independence, distributions, random variables, simulation, expectation, variance, covariance, transformations, law of large numbers, Central limit theorem, estimation, efficiency, maximum likelihood, Cramer-Rao bound, bootstrap. Prerequisites: MATH 118 or MATH 125 or MATH 126.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (747,'Dornsife','MATH','395',2,'Seminar in Problem Solving','Systematic approach to solving non-standard and competition level math problems on inequalities, infinite sums and products, combinatorics, number theory, and games. <strong>Recommended preparation:</strong> MATH 126 or MATH 127.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (748,'Dornsife','MATH','407',4,'Probability Theory','Probability spaces, discrete and continuous distributions, moments, characteristic functions, sequences of random variables, laws of large numbers, central limit theorem, special probability laws. Prerequisite: MATH 226 or MATH 227.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (749,'Dornsife','MATH','408',4,'Mathematical Statistics','Principles for testing hypotheses and estimation, confidence intervals, methods of moments, maximum likelihood, information inequality, likelihood ratio tests, goodness of fit and nonparametric methods.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (750,'Dornsife','MATH','410',4,'Fundamental Concepts of Modern Algebra','Sets; relations; groups; homomorphisms; symmetric groups; Abelian groups; Sylow\'s theorems; introduction to rings and fields. Not open to students with credit in MATH 470. Prerequisite: MATH 225. <strong>Recommended preparation:</strong> one 400-level Mathematics course, excluding MATH-450.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (751,'Dornsife','MATH','425a',4,'Fundamental Concepts of Analysis','The real number system, metric spaces, limits, continuity, derivatives and integrals, infinite series. Prerequisites: MATH 226 or MATH 227. <strong>Recommended preparation:</strong> One 400-level Mathematics course, excluding MATH 450.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (752,'Dornsife','MATH','430',4,'Theory of Numbers','Introduction to the theory of numbers, including prime factorization, congruences, primitive roots, N-th power residues, number theoretic functions, and certain diophantine equations. Prerequisites: MATH 126 or MATH 127.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (753,'Dornsife','MATH','434',4,'Geometry and Transformations','The Euclidean plane and its isometries. Models for the hyperbolic metric, isometries, linear fractional maps, geodesics. Gluing constructions. Knot theory and invariants. Prerequisite: MATH 226 or MATH 227.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (754,'Dornsife','MATH','440',4,'Topology','Cardinals, topologies, separation axioms. Compactness, metrizability, function spaces; completeness; Jordan curve theorem. <strong>Recommended preparation:</strong> Upper division Math course.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (755,'Dornsife','MATH','445',4,'Mathematics of Physics and Engineering II','Vector field theory; theorems of Gauss, Green, and Stokes; Fourier series and integrals; complex variables; linear partial differential equations; series solutions of ordinary differential equations. Prerequisite: (MATH-225 and MATH-226 or MATH-227) or MATH-245.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (756,'Dornsife','MATH','458',4,'Numerical Methods','Rounding errors in digital computation; solution of linear algebraic systems; Newton\'s method for nonlinear systems; matrix eigenvalues; polynomial approximation; numerical integration; numerical solution of ordinary differential equations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (757,'Dornsife','MATH','467',4,'Theory and Computational Methods for Optimization','Methods for static, dynamic, unconstrained, constrained optimization. Gradient, conjugate gradient, penalty methods. Lagrange multipliers, least squares, linear, nonlinear dynamic programming. Application to control and estimation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (758,'Dornsife','MATH','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (759,'Dornsife','MATH','499',2,'Special Topics','Lectures on advanced material not covered in regularly scheduled courses. No more than two registrations allowed. Prerequisite: Departmental Approval.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (760,'Dornsife','MATH','505a',3,'Applied Probability','Populations, permutations, combinations, random variables, distribution and density functions, conditional probability and expectation,; binomial, poisson, and normal distributions; laws of large numbers, central limit theorem. Prerequisite: Consent of Instructor.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (761,'Dornsife','MATH','507a',3,'Theory of Probability','a: Probability spaces; distributions and characteristic functions; laws of large numbers, central limit problems; stable and infinitely divisible laws; conditional distributions. b: Dependence, martingales, ergodic theorems, second-order random functions, harmonic analysis, Markov processes.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (762,'Dornsife','MATH','509',3,'Stochastic Differential Equations','Brownian motion, stochastic integrals, the Ito formula, stochastic differential equations, analysis of diffusion processes, Girsanov transformation, Feynmann-Kac formula, applications.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (763,'Dornsife','MATH','510a',3,'Algebra','A: Group Theory: Isomorphism theorems, group actions, Sylow\'s theorems, simple and solvable groups; Field Theory: Galois correspondence, radical extensions, algebraic and transcendental extensions, finite fields.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (764,'Dornsife','MATH','511aL',4,'Data Analysis','Major parametric and non-parametric statistical tools used in biomedical research, computer packages including SAS. Includes laboratory. Lecture, 2 hours; laboratory, 2 hours.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (765,'Dornsife','MATH','511bL',4,'Data Analysis','Statistical methods for analysis of categorical data including dichotomous, ordinal, multinomial and count data, using Stata package. Includes laboratory.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (766,'Dornsife','MATH','525a',3,'Real Analysis','a: Measure and integration over abstract measure spaces, Radon-Nikodym theorem, Fubini\'s theorem, convergence theorems, differentiation. Prerequisite: 425ab. b: Metric spaces, contraction principle, category, Banach spaces, Riesz representation theorem, properties of Lp, Hilbert spaces, orthogonal expansions, Fourier series and transforms, convolutions. Prerequisite: 525a.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (767,'Dornsife','MATH','530a',3,'Stochastic Calculus and Mathematical Finance','Stochastic processes revisited, Brownian motion, Martingale theory, stochastic differential equations, Feynman-Kac formula, binomial models, basic concepts in arbitrage pricing theory, equivalent Martingale measure. <strong>Recommended preparation:</strong> Math-225, Math-407. Duplicates credit in the former MATH-503.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (768,'Dornsife','MATH','532',3,'Combinatorial Analysis','Inversion formulas, generating functions and recursions, partitions, Stirling numbers, distinct representatives, Ramsey\'s theorem, graph theory, block designs, difference sets, finite geometrics, Latin squares, Hadamard matrices.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (769,'Dornsife','MATH','540',3,'Topology','Initial and final topologies, function spaces, algebras in C(Y), homotopy, fundamental group, fiber spaces and bundles, smashes, loop spaces, groups of homotopy classes, cw- complexes. Prerequisite: 440.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (770,'Dornsife','MATH','541b',3,'Introduction to Mathematical Statistics','Hypotheses testing, Neyman-Pearson lemma, generalized likelihood ratio procedures, confidence intervals, consistency, power, jackknife and bootstrap. Monte Carlo Markov chain methods, hidden Markov models.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (771,'Dornsife','MATH','544L',3,'Multivariate Analysis','Exploratory and inferential techniaues for multivariate data, Hotelling\'s T2, multivariate analysis of variance, classification analysis, principle components, cluster analysis, factor analysis. Involves computer use.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (772,'Dornsife','MATH','547',3,'Mathematical Foundations of Statistical Learning Theory','Binary classification, empirical risk minimization, support vector machines, voting algorithms and AdaBoost, Vapnik-Chervonenkis combinatorics, concentration-of-measure inequalities, sparse recovery problems, high-dimensional convex geometry.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (773,'Dornsife','MATH','555a',3,'Partial Differential Equations','Second-order partial differential equations of elliptic, parabolic, and hyperbolic type; in particular, potential and wave equations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (774,'Dornsife','MATH','570a',3,'Methods of Applied Mathematics','Metric spaces, compactness, completion, fixed-point theorems, abstract spaces, linear operators, functionals, duality, conjugates, Lp spaces, Fourier transform. Applications from Engineering, Natural and Social sciences. <strong>Recommended preparation:</strong> Linear Algebra and Calculus.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (775,'Dornsife','MATH','578b',3,'Computational Molecular Biology','Applications of the mathematical, statistical and comutational sciences to data from molecular biology. Statistics for genomic sequence data: DNA sequence assembly, significance of alignment scores, hidden Markov models, genetic mapping, models of sequence evolution, and microarray analysis.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (776,'Dornsife','MATH','580',3,'Introduction to Functional Analysis','Basic functional analysis in Banach and Hilbert spaces. Weak topologies, linear operators, spectral theory, calculus of vector-valued functions. Banach algebras.',1);
