INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (807,'Dornsife','MDES','180gw',4,'The Middle East','Introduction to the history and the study of the Middle East from ancient to modern times.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (808,'Dornsife','MDES','301g',4,'The Global Middle East','Explores the modern Middle East by focusing on the process of globalization.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (809,'Dornsife','MDES','314p',4,'Political Thought in the Middle East','Major strands of Islamic political thought from early Islam through the classical Islamic period to contemporary developments and transformations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (810,'Dornsife','MDES','325g',4,'Politics of Film and Literature in Modern Iran','Explores contemporary Iranian society through the study of social tensions created by poverty-wealth, tradition-modernity, masculinity-femininity, and notions of religio-political authority as reflected in the literature and films of the country.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (811,'Dornsife','MDES','345p',4,'Power and Authority in the Middle East','A comparative examination of social relations of power and institutions with a special focus on intellectual traditions, the law, and social movements.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (812,'Dornsife','MDES','363',4,'Middle East Political Economy','Examination of general economic development issues: population, agriculture, industrialization, trade, oil, etc. Several Mideast case study countries are then explored in depth.',1);
