INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (814,'Dornsife','NEUR','421',4,'Neurobiology','Structure, function, and development of nervous systems; neural integration and mechanisms of behavior; organization and operation of brains.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (815,'Dornsife','NEUR','440',4,'Introduction to Cognitive Neuroscience','Introduction to the major components of cognition (perception, memory, intelligence) in terms of the neural coding characteristic of the relevant brain areas.',1);
