INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (820,'Dornsife','NSCI','524',4,'Advanced Overview of Neurosciences','Study of the nervous system at multiple levels through the analysis of four themes: Motor Control; Emotion, Motivation, and Decision-Making; Memory and Learning; and Vision. Prerequisites: BISC 421. Open only to master and doctoral students. (Duplicates credit in former NEUR 524.)',1);
