INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (937,'Dornsife','PHYS','690',3,'Introduction to Physical Biology','Introduces students to the role of physics in biology. Considers both experimental and more fundamental points of view. Explores a few current research directions. <strong>Recommended preparation:</strong> Good knowledge of basic statistical mechanics and thermodynamics.',1);
