INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (947,'Dornsife','POIR','555',4,'Democracy and Democratization in Comparative Perspective','Seminar generates and tests theories of democratization. Readings will focus on Europe, Latin America, Soviet Eurasia emphasizing core theoretical and methodological aspects. MA and other students outside of POIR must obtain faculty permission to register for the course.',1);
