INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (950,'Dornsife','POIR','593',2,'Practicum in Teaching Politics and International Relations','Practical principles for the long-term development of effective teaching within political science and international relations disciplines. Intended for teaching assistants in Dornsife College. Graded CR/NC. Open only to doctoral students.',1);
