INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (952,'Dornsife','POIR','610',4,'Research Design','The course will cover the design of experimental and observational research. We will examine both quantitative and qualitative approaches to social science research. Open only to doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (953,'Dornsife','POIR','611',4,'Introduction to Regression Analysis','The course will introduce students to regression analysis and its application to social science research. Open only to doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (954,'Dornsife','POIR','615',4,'Formal Models of Politics','Introduces basics of game theory and surveys foundational models of American, comparative, and international politics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (955,'Dornsife','POIR','618',4,'Problems of American Politics','Theoretical and methodological problems in American politics with emphasis on emerging research paradigms. MA and other students outside of POIR must obtain faculty permission to register for the course.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (956,'Dornsife','POIR','620',4,'American Politics and Policy Processes','Survey of literature; examination of approaches, concepts, and issues in the field of American politics and policy processes. Duplicates credit in former POSC 510. Open only to doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (957,'Dornsife','POIR','660',4,'Introduction to International Relations Theory','The primary objective of this course is to introduce Ph.D. students to theoretical and empirical issues related to the study of international relations. Open only to doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (958,'Dornsife','POIR','662',4,'Governance in International Relations','Rules and rule structures in international relations; major theoretical perspectives and empirical research. Open only to doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (959,'Dornsife','POIR','670',4,'International Political Economy','Survey of approaches to international political economy. Intellectual roots; the management of collective goods; North-South relations are examined. Open only to doctoral students.',1);
