INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (966,'Dornsife','PORT','220',4,'Portuguese III','Intensive work in listening comprehension, oral communication, reading and writing, with emphasis on free expression; readings related to Portuguese culture and civilization.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (967,'Dornsife','POSC','100g',4,'Theory and Practice of American Democracy','Theoretical, institutional, and functional aspects of American national, state, and local government and politics; contemporary issues. Recommended for freshmen and sophomores.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (968,'Dornsife','POSC','130g',4,'Law, Politics and Public Policy','Interaction between law and politics; overview of the American legal system; value conflicts and public policy questions which arise within it.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (969,'Dornsife','POSC','265gw',4,'Environmental Challenges','Examination of the challenges of environmental problem-solving at the personal, local, national and global scales, focused on the issue of climate change.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (970,'Dornsife','POSC','300',4,'Principles, Institutions, and Great Issues of American Democracy','Underlying principles of American democracy; major issues of contemporary public policy in national and state institutions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (971,'Dornsife','POSC','311',4,'Political Analysis','Methodological and theoretical problems of micro-analytic studies in political science. Techniques of data collection and assimilation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (972,'Dornsife','POSC','315',4,'Regulation of Elections and Political Finance','The role money plays in elections and public decisions: disclosure requirements, limits on campaign contributions and expenditures, regulation of radio/television time, tax incentives, public funding.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (973,'Dornsife','POSC','323',4,'Applied Politics','Provides students with the knowledge and skills necessary to become active in politics based on understanding the history, theory, and practices of public participation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (974,'Dornsife','POSC','334',4,'Interest Groups and Elite Behavior','Introduction to interest group and elite views of the American system, including recent interest group theory and findings and the general critiques of power distribution in American society.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (975,'Dornsife','POSC','351',4,'Middle East Politics','Political development in the Middle East, emphasizing historical, cultural, and socioeconomic conditions affecting political structures and functions; modernization and countervailing social, economic and religious forces.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (976,'Dornsife','POSC','352',4,'Politics of Southeast Asia','Theories of development and nation-building; revolutionary and evolutionary modernization; role of history, culture, socioeconomic conditions in affecting political structures and functions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (977,'Dornsife','POSC','356',4,'Politics in the People\'s Republic of China','The Chinese revolution; social, political, and economic developments in post-1949 China; China after Mao Zedong (Mao Tse-tung).',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (978,'Dornsife','POSC','358',4,'Politics of Sub-Sahara Africa','Theories of development and nation-building; revolutionary and evolutionary modernization; role of history, culture, socioeconomic conditions in affecting political structures and functions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (979,'Dornsife','POSC','360',4,'Comparative Political Institutions','Institutions and processes of advanced industrial societies; political culture, interest articulation and aggregation, the governmental process.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (980,'Dornsife','POSC','370',4,'European Political Thought I','Basic concepts of Western political thought from Plato through the contract theorists.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (981,'Dornsife','POSC','380',2,'Political Theories and Social Reform','Political theories and philosophies in modern times and their relation to public policy and social reform.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (982,'Dornsife','POSC','391',4,'Honors I: Undergraduate Seminar','Selected topics in designated area of political science. Discussion of readings and presentation of papers.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (983,'Dornsife','POSC','395',2,'Directed Governmental and Political Leadership Internship','Intensive experience in governmental and political offices. Minimum time requirement; evaluation by office and intern report required.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (984,'Dornsife','POSC','398',1,'Trial Advocacy: Theory and Practice','Course covering substantive law, evidence, public speaking and use of societal mores in courtroom advocacy. Open only to Mock Trial team members.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (985,'Dornsife','POSC','410x',2,'Political Fellows Seminars','Political practitioners will teach on topics such as political strategy, campaigns and speechwriting.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (986,'Dornsife','POSC','418',4,'Environmental Sustainability and Public Participation','Examines civic participation in international, national, and local environmental politics and sustainability efforts.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (987,'Dornsife','POSC','423',4,'Presidents and the Presidency','Presidential coalition; sources of presidential power; recent leadership styles; decision-making within the presidency.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (988,'Dornsife','POSC','425',4,'Legislative Process','Individual behavior and decision-making within legislatures; changing executive-legislative functions; legislative functions; relationships to political systems in comparative perspective.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (989,'Dornsife','POSC','426',4,'The United States Supreme Court','Role of the Court in American politics; overview of major decisions; the politics of appointment; the process of decision-making; impact of judicial decisions. <strong>Recommended preparation:</strong> POSC 130',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (990,'Dornsife','POSC','442m',4,'The Politics of Human Differences: Diversity and Discriminat','A comparative perspective on social and cultural forces that affect American laws and policies concerning discrimination on the basis of race or ethnicity, gender, sexual orientation, age, and disability.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (991,'Dornsife','POSC','444',4,'Civil and Political Rights and Liberties','An examination of debates and controversies surrounding the nature and scope of civil rights and civil liberties. <strong>Recommended preparation:</strong> POSC 340 or POSC 440',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (992,'Dornsife','POSC','450',4,'Political Development','Choice of models in nation- building; party and other means of mass mobilization; elite recruitment and differentiation; peculiarities of cultures and subcultures; integration of ethnic and other minorities; political socialization and secularization; legitimization.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (993,'Dornsife','POSC','451',4,'Politics of Resources and Development','Comparison of relationships between rich and poor countries involving political and economic resources and prospects for development; impact on industrialized states; interdependence; new international economic order.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (994,'Dornsife','POSC','469',4,'Critical Issues in Comparative Politics','Intensive examination of critical issues of particular interest in the field of comparative politics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (995,'Dornsife','POSC','476',4,'Contemporary Political Thought','Twentieth century political philosophy dealing with major movements in psychological, existential, socialist, and nationalist thought as they bear upon the crisis of political authority in our time.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (996,'Dornsife','POSC','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (997,'Dornsife','POSC','590',1,'Directed Research','Research leading to the master\'s degree. Maximum units which may be applied to the degree to be determined by the department. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (998,'Dornsife','POSC','790',1,'Research','Research leading to the doctorate. Maximum units which may be applied to the degree to be determined by the department. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (999,'Dornsife','PSYC','100Lg',4,'Introduction to Psychology','Introduction to psychological science, including historical and contemporary approaches. Behavior examined from biological, cognitive, social, developmental, and personality perspectives. Disorders and treatments.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1000,'Dornsife','PSYC','165Lg',4,'Drugs, Behavior, and Society','An integrative systems perspective of drugs; including their historical, economic, and cultural importance, psychopharmacology, addiction, relationship to crime, and therapeutic use in treating psychological disorders.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1001,'Dornsife','PSYC','240xg',4,'Scientific Inquiry and Reasoning in Health Care','Critical analysis and reasoning skills required to solve scientific problems in human behavior, including presentation of data, logic of research design, statistics, and research ethics. Not for major credit for Psychology majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1002,'Dornsife','PSYC','274Lg',4,'Statistics','Introduction to the use of statistics in psychology: basic ideas in measurement; frequency distributions; descriptive statistics; concepts and procedures in statistical inference.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1003,'Dornsife','PSYC','275Lg',4,'Language and Mind','Language within cognitive science: speech physiology and acoustics, language acquisition, reading, language disorders, perception and mental representation of words, linguistic diversity and computer analysis of speech.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1004,'Dornsife','PSYC','290x',2,'Supervised Research in Psychology','Supervised Research with department faculty . Not for major or minor credit. Prerequisite: PSYC 100',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1005,'Dornsife','PSYC','301L',4,'Cognitive Processes','Experimental and theoretical aspects of human memory, perception, thinking, and language. Lectures, demonstrations, and individual experiments. Prerequisite: 100g or departmental approval.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1006,'Dornsife','PSYC','304L',4,'Sensation and Perception','Receptor processes and stimulus organization; traditional topics in the perception of objects, space, time. Laboratory demonstrations and exercises.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1007,'Dornsife','PSYC','305',4,'Learning and Memory','Principles involved in classical and operant conditioning. Concentration on basic causes of behavior; consideration of the relevance of simple behavioral laws to complicated human behavior.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1008,'Dornsife','PSYC','314L',4,'Experimental Research Methods','Experimental research methods in psychology; nature and concepts of scientific method. Lab exercises, data analysis and preparation of APA style empirical report.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1009,'Dornsife','PSYC','316L',4,'Non-Experimental Research Methods','Non-experimental research methods in psychology. Observational, survey and data analysis exercises',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1010,'Dornsife','PSYC','326',4,'Behavioral Neuroscience','Neural bases of behavior. Concentration on sensory and motor processes and the interaction of neural, chemical, and hormonal systems.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1011,'Dornsife','PSYC','336L',4,'Developmental Psychology','Child and adolescent behavior and associated theories; exploration of the continuity between child and adult behavior. Laboratory projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1012,'Dornsife','PSYC','339Lg',4,'Origins of the Mind','Exploration of ancient philosophical questions concerning the origins of human knowledge through empirical studies of infants, animals, and adults from diverse cultures.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1013,'Dornsife','PSYC','353g',4,'Close Relationships','Scientific perspective of close relationships: intimate relationships, friendships and others, evolutionary and biological bases of attraction and love, historical, social , cultural influences. Prerequisite: PSYC 100. (Duplicates credit in PSYC 359)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1014,'Dornsife','PSYC','355',4,'Social Psychology','Theoretical and experimental analysis of human behavior. Social processes involved in attitudes, conformity, compliance, interpersonal perception, liking, affiliation, aggression, altruism, and group dynamics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1015,'Dornsife','PSYC','360',4,'Abnormal Psychology','The commonly diagnosed behavior pathologies; biological, social, cultural, and developmental antecedents of abnormal behavior; principles of learning, perception, and motivation, as they relate to psychopathology.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1016,'Dornsife','PSYC','363',4,'Criminal Behavior','Genetic, biological, psychological, and sociological characteristics of those who evidence criminal behavior; theoretical formulations to be reviewed and appraised. Prerequisites: PSYC 100. Duplicates credit in former PSYC 463.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1017,'Dornsife','PSYC','367g',4,'Stress, Health, and the Mind-Body Connection','Introduction to psychological, biological, and behavioral processes affecting physical health, including stress, coping with disease, health behaviors, and socioeconomic and cultural influences on health. Prerequisite: PSYC 100.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1018,'Dornsife','PSYC','412',4,'Current Topics In Social Psychology','Extensive examination of select current research in social psychology, based on original research papers. Specific topics change by semester.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1019,'Dornsife','PSYC','421L',4,'Data Analysis for Psychological Research','Multivariate analysis emphasizing model estimation and testing; topics vary, e.g., multiple regression, logistic regression, factor analysis, multilevel linear modeling, structural equation modeling, multiway frequency analysis.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1020,'Dornsife','PSYC','422',4,'Human Judgment and Decision Making','Descriptive and normative models of decision making; topics include probability judgments, inference, correlation, emotion, mental accounting, decision analysis, lens model, equity, social dilemmas, time, risk.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1021,'Dornsife','PSYC','426',4,'Motivated Behaviors and Addiction','Social, environmental, and physiological influences on behaviors associated with aggression, eating, reproduction, and sleep. Will focus on behavioral disorders such as addiction.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1022,'Dornsife','PSYC','430',4,'Social Development of Infants, Children and Adolescents','An analysis of selected topics and issues in child social development. <strong>Recommended preparation:</strong> PSYC 274, PSYC 314, PSYC 336.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1023,'Dornsife','PSYC','440',4,'Introduction to Cognitive Neuroscience','Introduction to the major components of cognition (perception, memory, intelligence) in terms of the neural coding characteristic of the relevant brain areas.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1024,'Dornsife','PSYC','450',4,'Neural Network Models of Social and Cognitive Processes','Introduction to using neural network or connectionist models to simulate cognitive, social, emotional and motivational processes; basic concepts and tools in computational neuroscience.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1025,'Dornsife','PSYC','459',4,'Industrial/Organizational Psychology','I/O Psychologists develop and apply scientifically supported solutions to the workplace. \'Industrial\' deals with human resource functions, and \'Organizational\' with psychological aspects of the organization. Prerequisite: PSYC 100. <strong>Recommended preparation:</strong> PSYC 316.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1026,'Dornsife','PSYC','462m',4,'Culture and Mental Health','The influence of culture, ethnicity, race and gender on human behavior. Mental health issues relevant to ethnic minorities in the U.S. <strong>Recommended preparation:</strong> sophomore standing or higher; PSYC-100.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1027,'Dornsife','PSYC','464',4,'Psychology of Marriage and the Family','Theories and research on family relationships across the life span, including research methods, cultural and developmental perspectives, communication, conflict, attachment, individual psychopathology, and family violence.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1028,'Dornsife','PSYC','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1029,'Dornsife','PSYC','501L',4,'Classic and Modern Statistical Methods I','An introduction to classic statistical techniques as well as modern robust methods for dealing violations of standard assumptions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1030,'Dornsife','PSYC','503L',4,'Regression and the General Linear Model','Multiple regression as a tool in experimental and non-experimental data; analysis of variance and covariance as regression on coded variables. Computer applications. Laboratory exercises.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1031,'Dornsife','PSYC','505',4,'Research Methods in Applied Social Psychology','Various research techniques that are useful in a variety of different real world settings, such as business, governmental agencies and charities. Open only to M.S., Applied Psychology students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1032,'Dornsife','PSYC','506',4,'Learning and Cognition','Survey of learning theory and research, including conditioning and information-processing approaches with human and animal subjects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1033,'Dornsife','PSYC','512',4,'Seminar in Social Psychology','Problems and theories of the person in the social context. Person perception, interpersonal relations, attitude dynamics, social systems.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1034,'Dornsife','PSYC','515',4,'Clinical Assessment','Study of clinical assessment: test construction, measurement and predicition of behavior, major cognitive and personality assessment instruments. (One of 4 clinical psychology core courses: 514, 515, 619, 620.)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1035,'Dornsife','PSYC','517',4,'Group Dynamics and Leadership','Theory and research on effective teams and characteristics of strong leaders. Negotiation, morale-building, managing expectancies, utilization of cultural diversity as a strength. Open only to M.S., Applied Psychology students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1036,'Dornsife','PSYC','521',4,'Cross Cultural Psychology in Applied Settings','Examination of major theoretical frameworks mapping worldwide cultures and values with a focus on applying thatknowledge in organizations. Open only to master students in Applied Psychology.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1037,'Dornsife','PSYC','530',3,'Concepts and Principles of Behavior Analysis','Provides intensive training in behavioral principles and laws of learning and motivation. Focuses on broad application of principles, from basic research with nonhumans, to application across human clinical populations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1038,'Dornsife','PSYC','531',2,'Behavioral Assessment','Studies theory and application of behavioral methods for skill assessment, functional assessment of destructive behavior, and learner motivation. Discusses relative strengths and limitations of direct versus indirect methods.',1);
