INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1040,'Dornsife','PSYC','534',4,'Social and Emotional Development in Children','Theories of social and emotional development, including sociocultural perspectives. Analysis of research on temperament, social relationships, individuation and moral development from birth to adolescence. Open to graduate students in psychology.',1);
