INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1155,'Dornsife','SPAN','260',4,'Advanced Spanish: Arts and Sciences','Development of students\' oral and writing skills using literary and scientific materials; grammar review. Duplicates credit in the former SPAN 266.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1156,'Dornsife','SPAN','261',4,'Advanced Spanish: Society and the Media','Analysis of cultural issues in the Spanish-speaking world. Discussions, presentations, writing assignments, and grammar instruction designed to improve students\' proficiency in Spanish. Duplicates credit in the former SPAN 265.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1157,'Dornsife','SPAN','280x',2,'Conversational Spanish','Discussions of short films, cultural and literary texts and other activities designed to improve conversational skills. Prerequisite: SPAN 220. Not for credit for Spanish majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1158,'Dornsife','SPAN','290gp',4,'Introduction to Latin American and Iberian Studies','Introduction to the cultural history of Spain, Portugal and Latin America with a focus on literature and visual works from the early-modern period to the twenty-first century.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1159,'Dornsife','SPAN','301',4,'Introduction to Literature and Film','Introduction to critical reading and interpretation of poetry, narrative fiction, drama, and film from Spain and Latin America.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1160,'Dornsife','SPAN','310',4,'Structure of Spanish','A systematic study of the structure of Spanish. Topics include fundamental aspects of the sound system; word classes; sentences and their meaning; linguistic change and variation; standard and colloquial usage.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1161,'Dornsife','SPAN','315',4,'Advanced Grammar and Translation','Contrastive study of Spanish and English structures designed to explore the similarities and differences between the two languages and to familiarize students with translation techniques. Emphasis on a variety of text types with the aim of increasing linguistic and cultural appreciation of the Spanish language.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1162,'Dornsife','SPAN','316x',4,'Spanish for the Professions','The language and culture of a particular area of study or profession, such as medicine and healthcare, political and social sciences, business and the law. Limited to 4 units for major or minor credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1163,'Dornsife','SPAN','321',4,'Sounds, Images, Objects','Introduction to the study of Iberian and Latin American cultural forms through readings on the visual arts, cinema, architecture, and music. Conducted in Spanish.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1164,'Dornsife','SPAN','375',4,'Latin American Cultural and Literary Theory','Survey of cultural critique focused on Latin America as a cultural region and on Latin Americanism as a transnational academic practice.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1165,'Dornsife','SPAN','380g',4,'Literature of Mexico','Principal writers and their works from Colonial times to the present. Non-majors may write assignments in English. <strong>Recommended preparation:</strong> advanced comprehension of oral and written Spanish.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1166,'Dornsife','SPAN','413m',4,'Social and Geographic Varieties of Spanish','Historical, social, and cultural elements represented in the dialectal diversity of the Spanish language; fieldwork in bilingual communities in the United States. Majors prepare assignments in Spanish, non-majors in English. Conducted in Spanish and English.',1);
