INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1182,'Dornsife','SSCI','580',4,'Spatial Computing','Theoretical foundations, methods, techniques, and software systems for spatial computing, including geospatial semantic web and linked data, spatial data mining, geocoding, and document linking. <strong>Recommended preparation:</strong> SSCI 581 or INF 510.',1);
