INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1184,'Dornsife','SSCI','582',4,'Spatial Databases','Design, implementation, and interrogation of relational, object-oriented and other types of geospatial databases. <strong>Recommended preparation:</strong> SSCI 581.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1185,'Dornsife','SSCI','583',4,'Spatial Analysis','Provides the knowledge and skills necessary to investigate the spatial patterns which result from social and physical processes operating at or near the Earth\'s surface. <strong>Recommended preparation:</strong> SSCI 581. (Duplicates credit in former GEOG 583)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1186,'Dornsife','SSCI','584',4,'Spatial Modeling','The use of spatial models to describe social and environmental processes, patterns and systems at multiple spatial and temporal scales. <strong>Recommended preparation:</strong> SSCI 583.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1187,'Dornsife','SSCI','585',4,'Geospatial Technology Project Management','Concepts, principles, and use of project management tools and the people issues encountered running GIS projects. <strong>Recommended preparation:</strong> SSCI 581.',1);
