INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1197,'Dornsife','SSCI','680',4,'Advanced Spatial Computing','The role of spatial computing in understanding the world, in knowing and communicating our relationships to specific places, and for navigating through those places.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1198,'Dornsife','SSCI','693',2,'Practicum in Teaching the Liberal Arts','Principles of pedagogy for teaching the liberal arts, focusing on issues and technologies for spatial thinking. Required for first-semester teaching assistants in spatial sciences.',1);
