INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1208,'Dornsife','VISS','501',4,'Introduction to Visual Studies: Methods and Debates','A critical introduction to the field of visual studies focusing on interdisciplinary approaches to images, objects, and visual technologies as well as key texts and interpretive debates.',1);
