INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1216,'Arts,','ACAD','174',2,'Innovators Forum','Leaders in diverse disciplines, industries, and the arts present and discuss problems facing society, and critique real-world projects that challenge the concept of innovation.',1);
