INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1242,'Arts,','ACAD','341',3,'App Development for Phones and Tablets','Develop phone and tablet applications for opensource platforms such as Android that utilize the core functionality of mobile devices such as GPS, accelerometers, touch gestures.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1243,'Arts,','ACAD','342',3,'Mobile Application Development','Develop applications for mobile devices such as iPhones and iPads (iOS) and other smart phones (Android). Build a mobile application from start to finish.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1244,'Arts,','ACAD','344',3,'Advanced Topics in Mobile App Development','Advanced topics in mobile app development such as using REST services, security, cloud integration, NFC (near field communication), wireless networking for mobile apps, monetizing apps, and the latest frameworks to create advanced apps.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1245,'Arts,','ACAD','351',4,'Technologies and Practices for Analytics','Overview of the process of data analysis. Reporting, visualization and prediction. Practical applications of analytics through case studies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1246,'Arts,','ACAD','356',4,'Audio and Media Integration','Methods and techniques for integrating audio into various media and applications including: film and video, Internet streaming, mobile devices, and other digital and computer applications.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1247,'Arts,','ACAD','365',3,'Managing Data in C++','Overview of basic data structures and algorithms including linked lists, stacks, queues, binary trees, and hash tables.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1248,'Arts,','ACAD','375',4,'Business and Professional Communication','Oral and written communication skills demanded in the workplace including informative and persuasive speeches; interviewing; team communication; and training material preparation. <strong>Recommended preparation:</strong> COMM 204',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1249,'Arts,','ACAD','376',4,'Discerning and Making I: Concept and Feasibility','A broad look at innovation from the perspectives of designers, engineers, and entrepreneurs as applied to concept/feasibility of art, design, products, services and experiences.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1250,'Arts,','ACAD','385x',4,'Marketing of Creative Disruption and Innovation','Learn to use fundamental principles of marketing, branding, and consumer behavior to successfully market disruptively innovative products including goods, services, and ideas. Not available for degree or major credit for business and accounting majors. Duplicates credit in BUAD-307.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1251,'Arts,','ACAD','404',3,'Modern Technologies of Web Development','Provide students with the necessary skills to build web applications using modern techniques, frameworks, libraries, and tools that are used among developers within the industry.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1252,'Arts,','ACAD','419',2,'Professional Internship','An experiential/academic opportunity in an arts, technology and business related facility. Written analysis, evaluation, and working internship.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1253,'Arts,','ACAD','449',4,'Data Mining: Descriptive and Predictive','Examine and compare practical methods in data mining - data preparation, classification, clustering. Knowledge discovery process is to find patterns and regularities from large datasets.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1254,'Arts,','ACAD','475a',4,'The Garage Experience','Academy capstone experience, innovative projects leading to operational prototypes and viable enterprises, mentored by faculty and industry experts.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1255,'Arts,','ACAD','476',4,'Technologies for Interactive Marketing','Technologies, concepts and strategies in the emerging online advertising ecosystem. Strategies and tactics to drive traffic to a website and measure efficacy. Students will work with a real client and budget to craft and execute an online marketing plan.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1256,'Arts,','ACAD','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1257,'Arts,','ACAD','590',1,'Directed Research','Research leading to graduate degree. Maximum units which may be applied to degree determined by department.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1258,'Arts,','IDSN','505',0.5,'Intensive','Optional short-term, primarily residential, learning experience paired with an event/activity. Intensive experience aligns with and extends the cross-disciplinary nature of the program. <strong>Recommended preparation:</strong> IDSN 510 and IDSN 540. Open only to Integrated Design, Business and Technology majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1259,'Arts,','IDSN','510',2,'Integrative Practices Residential','One-week campus residential. Introduction to program themes, content, concepts, theories, and processes. Opportunity to build skills and meet cohort, with an emphasis on team building. Open only to Integrated Design, Business and Technology majors. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1260,'Arts,','IDSN','515',2,'Professional Practices Residential','One-week off-campus residential. Applied, intensive workshop format focused on solutions for real-world problems with input from industry experts. <strong>Recommended preparation:</strong> IDSN 510, IDSN 520, IDSN 525, IDSN 540 and/or IDSN 530. Open only to: Integrated Design, Business and Technology majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1261,'Arts,','IDSN','520',3,'Design Essentials','Overview of design language, process and product. Build fluency in applying core visual principles and design-based problem-solving techniques. Explore historical and contemporary design issues. <strong>Recommended preparation:</strong> IDSN 510. Open only to Integrated Design, Business and Technology majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1262,'Arts,','IDSN','525',3,'Business Essentials','Overview of general business and management concepts and techniques that builds theoretical and analytical fluency and identifies and examines leadership and managerial challenges. <strong>Recommended preparation:</strong> IDSN 510. Open only to Integrated Design, Business and Technology majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1263,'Arts,','IDSN','530',3,'Technology Essentials','Overview of core information technology concepts, methods, and frameworks. Develop aptitude in using tools, applications and systems; learn basic programming languages and methodologies. <strong>Recommended preparation:</strong> IDSN 510. Open only to Integrated Design, Business and Technology majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1264,'Arts,','IDSN','540',4,'Processes and Perspectives','Discipline-specific, cross-disciplinary, and shared approaches to products, services, projects, systems, organizational development. Topics include creative and user-focused perspectives, feasibility analytics, lean startup techniques.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1265,'Arts,','IDSN','545',2,'Integrative Project','Faculty-mentored project paired with a Core course. Demonstrate individual and simultaneous skills and competency across fields and within cross-functional, cross-disciplinary teams. <strong>Recommended preparation:</strong> IDSN 510. Open only to Integrated Design, Business and Technology majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1266,'Arts,','IDSN','550',4,'Opportunity and Uncertainty','Discipline-specific, cross-disciplinary, and shared approaches to recognizing/analyzing opportunities, identifying/framing problems, and developing/iterating solutions. Techniques gleaned from entrepreneurship, creative practices, critical thinking methods, ethnography, systems theory.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1267,'Arts,','IDSN','570',4,'Decisions and Solutions','Generate portfolio of solutions for a given \'problem.\' Explore solutions and decisions, mechanisms for reducing uncertainty and risk. Includes quantitative and qualitative techniques.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1268,'Marshall','ACCT','370',4,'External Financial Reporting Issues','Understanding of decision-making, problem solving, and research skills as a supplement to financial accounting knowledge for accounting professionals. Prerequisites: BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1269,'Marshall','ACCT','371',4,'Introduction to Accounting Systems','Understanding of technology and controls needed to capture data used by professionals in financial and managerial accounting, auditing and taxation. Prerequisite: BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305. Duplicates credit in former ACCT-371ab.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1270,'Marshall','ACCT','372',2,'Internal Reporting Issues','Understanding of decision-making, problem solving, and research skills as a supplement to managerial accounting knowledge for accounting professionals. Prerequisites: BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1271,'Marshall','ACCT','373',2,'Introduction to Auditing and Assurance Services','Exploration of the requisite skills and knowledge needed to offer services in assurance, attestation or auditing engagements. Prerequisites: ACCT 370 and ACCT 371.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1272,'Marshall','ACCT','374',2,'Introduction to Tax Issues','Basic tax principles, introduction to U.S. federal, state and local tax systems, income and expense definitions, property transactions, and fundamentals in individual taxation. Prerequisites: BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1273,'Marshall','ACCT','377',2,'Valuation for Financial Statement Purposes','Explores Accounting Standards Codification (ASC) 820 Fair Value Measurements and Disclosures and reviews the historical background of US GAAP fair value guidance. Prerequisite: ACCT 370.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1274,'Marshall','ACCT','380x',4,'Introduction to Enterprise Risk Management','Understand the concept of risk, risk assessment and management frameworks; learn to identify, analyze, manage and mitigate and communicate this risk to stakeholders.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1275,'Marshall','ACCT','385x',4,'Introduction to Risk Management and Insurance','Understand the underlying economics, structure, and business models of an organization (often an insurance carrier) willing to accept risk for diverse situations: property, life, healthcare.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1276,'Marshall','ACCT','410x',4,'Foundations of Accounting','Non-technical presentation of accounting for users of accounting information; introduction to financial and managerial accounting. Not open to students with course credits in accounting. Not available for unit or course credit toward a degree in accounting or business administration.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1277,'Marshall','ACCT','415',4,'Intermediate Financial Accounting for Non-Accounting Majors','In-depth study of balance sheet, income statement, and cash flow statement issues from the perspective of a user (not preparer) of corporate financial reports. Prerequisite: ACCT-410 or BUAD-281 or BUAD-285b or BUAD-286b or BUAD-305. Not open to accounting majors. Duplicates credit the former ACCT-411x and ACCT-412x.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1278,'Marshall','ACCT','416',2,'Financial Reporting and Analysis','Examination of the role of financial statement analysis in the prediction of a firm\'s future financial performance.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1279,'Marshall','ACCT','430',4,'Accounting Ethics','Theoretical knowledge and practical application accountants need to identify ethical issues and reconcile conflicts among competing stakeholders\' interests in all major areas of accounting practice.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1280,'Marshall','ACCT','462',2,'Detecting Fraudulent Financial Reporting','Understanding/identifying methods of fraudulent financial reporting, and signals that financial statements were fictitiously prepared; major frameworks for analyzing ethical dilemmas; reforms in corporate reporting environment.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1281,'Marshall','ACCT','470',4,'Advanced External Financial Reporting Issues','Develop capabilities to identify and resolve advanced external financial reporting challenges, focusing primarily on operating, financing, and investing activities of business enterprises. Duplicates credit in ACCT 470ab.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1282,'Marshall','ACCT','473',2,'Financial Statement Auditing','Course builds on the background developed in ACCT 373, specifically the process used by external auditors to conduct financial statement audits. Prerequisites: ACCT-373.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1283,'Marshall','ACCT','474',2,'Tax Issues for Business','Capabilities to identify and articulate tax issues related to a business entity\'s life: formation, investing, financing and operations, and change of form.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1284,'Marshall','ACCT','490x',1,'Directed Research','Individual research and readings. Not available for graduate credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1285,'Marshall','ACCT','494',2,'Marshall Honors Research and Thesis','Experience in conducting research and writing a thesis under supervision of a faculty adviser.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1286,'Marshall','ACCT','499',1,'Special Topics','Selected topics reflecting current trends and recent developments in the field of accounting. Topics vary each semester.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1287,'Marshall','ACCT','509',4,'Concepts of Financial and Management Accounting','Introduction to the concepts of financial and managerial accounting. The course will provide coverage of key concepts needed by managers of businesses in order to communicate information important in decision-making. (Duplicates credit in GSBA 510 and GSBA 518.) Not open to business or accounting majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1288,'Marshall','ACCT','528',1.5,'Fair Value Accounting: GAAP, IFRS and Emerging Issues','Case study approach to explore fair value issues in accounting; research and analysis of causes of valuation differences.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1289,'Marshall','ACCT','532',1.5,'Financial Accounting for Mergers and Acquisitions','Practical problems in accounting for business combinations. Consolidated financial statements: fair value, acquisition and equity basis accounting. Duplicates credit in former ACCT 582.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1290,'Marshall','ACCT','533',1.5,'Mergers and Acquisitions: Tax Planning and Strategy','Tax planning and strategy in corporate restructuring including mergers, acquisitions, and divestitures. Tax background not required to be successful in this course. Duplicates credit in former ACCT 582.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1291,'Marshall','ACCT','541L',3,'Auditing in the Enhanced Data Age','Framework of the audit model including use of large data sets with automated audit tools. A lab environment is an integral part of the course.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1292,'Marshall','ACCT','546',3,'Auditing and Assurance Services','Concepts and principles governing independent professional services that provide assurance on the reliability and relevance of information, including financial statement information. Topics include demand and supply issues for these services, basic principles of evidence, risk assessment and testing. <strong>Recommended preparation:</strong> ACCT-525x.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1293,'Marshall','ACCT','547',3,'Enterprise Information Systems','Focuses on accounting enterprise database models and information technology required to support those systems. Includes analysis and design of interfunctional process flows through reengineering to exploit technology capabilities.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1294,'Marshall','ACCT','548L',3,'IT Audit and Data Applications','Exploration of the role systems play in organizations, the technology that supports these systems and issues relating to technology risk, system/application security and system review/audit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1295,'Marshall','ACCT','549',3,'Advanced Enterprise Systems and Technologies','Design, control and development of advanced enterprise systems, using reengineering, focusing on accounting and financial systems, using a wide range of emerging existing technologies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1296,'Marshall','ACCT','550t',3,'Tax Research and Professional Responsibilities','Tax law research methods; interpreting statutes, cases and rulings; communicating research results; administration and professional responsibilities of tax practice.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1297,'Marshall','ACCT','551t',3,'Taxation of Partnerships and S-Corps','Federal taxation of flow-through entities, including:partnerships, S corporations, limited liability partnerships(LLPs), and limited liability companies (LLCs). <strong>Recommended preparation:</strong> ACCT-550T or ACCT-560T or LAW 600.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1298,'Marshall','ACCT','559',3,'Strategy and Operations Through CFO Lens','Examination of strategic objectives and operations within specific industries and companies. Chief Financial Officers present how they view the business as a whole and measure performance effectively.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1299,'Marshall','ACCT','560t',3,'Tax Theory and Ethics','Taxation and its relationship to business and investment decisions; the effects of taxation on business organization, capital structure, policies, operation, and expansion.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1300,'Marshall','ACCT','561t',3,'Income Tax of Corporations and Their Shareholders','Concepts and principles governing the taxation of corporations and their shareholders; the effect of taxes on corporate formation, capital structure, distributions, and liquidations. <strong>Recommended preparation:</strong> ACCT-550T or ACCT-560T or LAW-600.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1301,'Marshall','ACCT','563t',3,'Federal Estate and Gift Taxes','Taxation of decedents\' estates and lifetime gifts; valuation of property subject to estate and gift taxes.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1302,'Marshall','ACCT','568t',3,'International Taxation','Taxation of cross-border transactions of U.S. citizens and corporations and of U.S. source income of foreign persons and corporations; planning for organization of international operations under the tax laws.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1303,'Marshall','ACCT','581',3,'Financial Statement Analysis','Analysis of corporate financial reports from a decision-maker\'s perspective. This course is case-and-applications-oriented. Applications include credit analysis, equity valuation, and financial distress. Duplicates credit in ACCT 527.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1304,'Marshall','ACCT','583',3,'Income Tax Accounting and Auditing','Examination of FAS 109 and roles of auditors, tax professionals and corporate financial personnel in preparing, analyzing and reviewing accrual of income taxes. Prerequisite: ACCT 561T or ACCT 585. Open only to graduate students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1305,'Marshall','ACCT','587',1.5,'Forensic Accounting','Role of the accountant in litigation matters. Identification and exploration of the analytical and communication tools necessary to be an effective forensic accountant. Prerequisite: ACCT 572.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1306,'Marshall','ACCT','590',1,'Directed Research','Research leading to the master\'s degree. Maximum units which may be applied to the degree to be determined by the School of Accounting. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1307,'Marshall','ACCT','605',3,'Survey of Financial Reporting Research','Survey of major topics and methods in research on financial reporting with coverage of both key research questions and frequently used methods. Open only to Business Administration doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1308,'Marshall','ACCT','608',3,'Positive Accounting Research','Survey of major topics related to positive accounting research with coverage of both key research questions and frequently used methods.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1309,'Marshall','ACCT','611',1.5,'Selected Topics in Accounting Research','Advanced Seminar to address issues/topics covered in accounting research. Open only to doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1310,'Marshall','ACCT','621',1,'Research Forum','Seminar. Review and discuss current research in Accounting. Presentations by faculty, visiting researchers, and advanced students. Open only to Marshall Ph.D. students. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1311,'Marshall','BAEP','423',4,'Management of Small Businesses','Strategic, organizational, financial, and human issues facing the small business. (Duplicates credit in BAEP 310, BAEP 450, BAEP 451, BUAD 301)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1312,'Marshall','BAEP','450',4,'Fundamentals of Entrepreneurship','Starting and managing one\'s own business: developing a viable concept, organizing the enterprise, market and financial planning, and controlling the organization. (Duplicates credit in BAEP 310, BAEP 423, BAEP 451, BUAD 301)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1313,'Marshall','BAEP','451',4,'The Management of New Enterprises','Development of analytical and conceptual skills in entrepreneurship and venture management. (Duplicates credit in BAEP 310, BAEP 423, BAEP 450, BUAD 301)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1314,'Marshall','BAEP','452',4,'Feasibility Analysis Feasibility Analysis','Students develop, analyze, and validate entrepreneurial concepts (including marketing, operational, and financial considerations) using customer feedback and risk assessment to conclude worthiness to pursue. Prerequisite: BAEP 310 or BAEP 423 or BAEP 450 or BAEP 451 or BUAD 301.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1315,'Marshall','BAEP','453',4,'Venture Management','Design and application of organization structures and systems in management of new ventures.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1316,'Marshall','BAEP','460',2,'Seminar in Entrepreneurship','Perspectives into the art and science of entrepreneurship under the guidance of a master instructor. Specific topics vary.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1317,'Marshall','BAEP','465',2,'Digital Playbook for Entrepreneurs: Creating a Tech Startup','Learn to use digital tools and technologies, such as social media, mobile, cloud computing, and e-commerce, to start and grow entrepreneurial ventures.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1318,'Marshall','BAEP','469',2,'Growth Hacking: Scaling Startups','Accelerate the growth of an entrepreneurial business with applied analytics tools and methods.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1319,'Marshall','BAEP','474',2,'The Entrepreneur\'s Guide to Intellectual Property','The fundamentals of intellectual property -- its value, its basic workings, and its role in entrepreneurship, business in general, science, the arts, and the professions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1320,'Marshall','BAEP','475',2,'Entertainment Entrepreneurship','Explore the current entrepreneurial trends and opportunities in the entertainment industry and uncover the key success factors for entrepreneurs in this industry.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1321,'Marshall','BAEP','480',4,'Entrepreneurial Family Business','Explores the dynamics of family and privately held businesses. Exploring generational and extended family issues, opportunities and obstacles faced in today\'s environment.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1322,'Marshall','BAEP','491',4,'Introduction to Social Entrepreneurship','Analysis of social enterprise models from micro-finance to job development. Analysis of basic issues regarding the difference between socially responsible companies, for-profit, and non-profit-run enterprises',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1323,'Marshall','BAEP','495',1,'Practicum in Business Issues (Internship)','Combined classroom discussion and field application of business theories and practices; part-time internship employment. Project to be jointly defined by student, employer and professor. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1324,'Marshall','BAEP','551',3,'Introduction to New Ventures','Study and development of analytical and conceptual skills in the management of new enterprises and new ventures within large organizations. Duplicates credit in BAEP 549, BAEP 550, GSBA 535, and GSBA 550ab.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1325,'Marshall','BAEP','552',3,'Cases in Feasibility Analysis','Study of analytical techniques used to evaluate business concepts and new business development. Duplicates credit in BAEP-566.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1326,'Marshall','BAEP','553',1.5,'Cases in New Venture Management','Cases and readings expose students to the challenges of developing long-range strategies for entrepreneurial ventures. Case work emphasizes developing new industries, growth through strategic alliances, and issues involved in the long-term strategic positioning of emerging companies.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1327,'Marshall','BAEP','556',3,'Technology Feasibility','Learn critical thinking and analytical skills they need to evaluate, value, and manage technology as intellectual property. Understand the technology commercialization process, use data mining and assessment techniques for patent databases, and study the unique business issues facing high technology start-ups.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1328,'Marshall','BAEP','558',1.5,'The Entrepreneurial Advisor: Problem Solving for Early-Stage Companies','Experiential course designed to develop skills in framing and solving complex problems in young companies. Apply skills to real ventures participating in course projects.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1329,'Marshall','BAEP','559',3,'Investing in New Ventures','Focus on the entrepreneurial skill set applied to new venture opportunities. Taught from the business plan reader\'s point of view; focus on selecting opportunities, structuring the relationship, adding value and realizing the value of that investment.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1330,'Marshall','BAEP','566',3,'Cases in Feasibility Analysis for Social Ventures','Study of analytical techniques used to evaluate business concepts with a specific focus on the development of a new social venture. Corequisite: BAEP 591. (Duplicates credit in BAEP 552.)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1331,'Marshall','BAEP','571',3,'Social Innovation Design','Use innovative problem-solving approaches to design radically affordable solutions to challenges faced by under-resourced communities. Engage in early-stage market and rapid prototyping.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1332,'Marshall','BAEP','590',1,'Directed Research','Research leading to the master\'s degree. Maximum units which may be applied to the degree to be determined by the department. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1333,'Marshall','BAEP','592',0.5,'Field Research in Business Entrepreneurship','Individual or team projects studying the business practices of an entrepreneurial industry, company, government agency, country, etc. Proposal, data collection, analyses, and written report. (<strong>Recommended preparation:</strong> completion of required MBA, MAcc, MBT, MKT, MSEI or MSSE course work)',1);
