INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1339,'Marshall','BUAD','100x',2,'Foundations of Finance and Accounting','Economic concepts underlying finance: scarcity, choice, and market resource allocation. Basics of financial asset valuation. Consideration of time-value of money and evaluation of risk. Not available for major or minor credit. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1340,'Marshall','BUAD','101',2,'Freshman Leadership Seminar','Colloquium of leading researchers, authors, and administrators in the Marshall School of Business and other schools at USC. Industry leaders will also be invited to talk about leadership challenges. Graded CR/NC. Open by invitation only to freshman business and accounting majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1341,'Marshall','BUAD','200x',2,'Economic Foundations for Business','Course examines fundamental concepts of both microeconomics and macroeconomics as they pertain to business and financial decisions. Not available for degree credit to business majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1342,'Marshall','BUAD','201x',4,'Introduction to Business for Non-Majors','Introduction to the principles and practices of businesses, sequence of exercises developing the basic skills, and influence of the economy on business and individual decisions. Not available for credit for business or accounting majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1343,'Marshall','BUAD','215x',4,'Foundations of Business Finance','Principles and practices of modern financial management; use of financial statements; valuation of investment; asset pricing under uncertainty; elements of financial decisions. Prerequisites: ACCT 410 or BUAD 280 or BUAD 285a or BUAD 305. Duplicates credit in BUAD 306.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1344,'Marshall','BUAD','252',2,'Choosing and Planning a Future Career in Business','Explore and plan for future career choices and options in business. Learn theoretical principles, best practices and strategies, and contemporary workplace issues/trends. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1345,'Marshall','BUAD','280',3,'Introduction to Financial Accounting','Emphasis on understanding financial statements and the impact of business transactions on information presented to management and interested stakeholders. Duplicates credit in BUAD 250a, BUAD 250b, BUAD 285a, BUAD 286a, BUAD 286b, BUAD 305.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1346,'Marshall','BUAD','281',3,'Introduction to Managerial Accounting','An emphasis on how accounting information, combined with a variety of techniques and best practices, supports enhanced management decision making. Duplicates credit in BUAD 285a, BUAD 285b, BUAD 286b, BUAD 305.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1347,'Marshall','BUAD','301',3,'Technology Entrepreneurship','Starting and managing a technological business: developing a viable concept, market and financial planning, product development, organizing the venture, protecting intellectual property rights. (Duplicates credit in BAEP 310, BAEP 423, BAEP 450, BAEP 451)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1348,'Marshall','BUAD','302',4,'Communication Strategy in Business','Theory, practice, and techniques of business communication strategy essential to external and organizational communication; group and interpersonal communication; development of skill in oral and written communication.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1349,'Marshall','BUAD','302t',4,'Communication Strategy in Accounting','Theory, practices, and techniques essential to communication in accounting. Interpersonal and group communication; oral presentations; writing; use of communication technologies; communication strategies for varied audiences. Duplicates credit in BUAD 302. Open only to accounting and prospective accounting majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1350,'Marshall','BUAD','304',4,'Organizational Behavior and Leadership','The role of leadership in business organizations; concepts and skills for managing oneself and others.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1351,'Marshall','BUAD','305',4,'Abridged Core Concepts of Accounting Information','Uses of accounting information in decision-making; accounting issues concerning income and cash flows, economic resources and capital. Duplicates credit in BUAD 280, BUAD 281, BUAD 285a, BUAD 285b, BUAD 286a and BUAD 286b.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1352,'Marshall','BUAD','306',4,'Business Finance','Financial problems of business enterprise; function of financial manager; sources of funds; instruments, institutions, and practices of finance; problems of financial management using case studies. Prerequisites: ACCT 410 or BUAD 280 or BUAD 285a or BUAD 286b or BUAD 305 and ECON 351. Duplicates credit in BUAD 215x.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1353,'Marshall','BUAD','307',4,'Marketing Fundamentals','Develops a managerial viewpoint in planning and evaluating marketing decisions of the firm: products, pricing, channels, promotion, information processing, legal implications, and marketing in contemporary society. Duplicates credit in MKT-385.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1354,'Marshall','BUAD','310g',4,'Applied Business Statistics','Statistical methods for business analysis; data exploration and description; sampling distributions; estimation; hypothesis testing, simple and multiple regression; model building. Extensive computer applications. (Duplicates credit in ITP 251)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1355,'Marshall','BUAD','311',4,'Operations Management','Fundamentals of operations management. Skills needed to analyze, manage, and improve business processes. Topics include: process, capacity, service, and inventory management and optimization. Duplicates credit in BUAD 311T.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1356,'Marshall','BUAD','311t',4,'Operations Management for Accounting Majors','Learn the fundamentals of operations management and acquire skills to analyze, measure, control, and improve production processes. Open to accounting majors only.(Duplicates credit in BUAD 311.)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1357,'Marshall','BUAD','315x',2,'Basics of Project and Operations Management for Non-Majors','Introduction to tools and methods for the design, production, and delivery of goods and services. Techniques for planning, monitoring, and controlling complex projects. Not available for degree or major credit for business and accounting majors. Duplicates credit in BUAD 311 and BUAD 311T.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1358,'Marshall','BUAD','380x',4,'Introduction to Enterprise Risk Management','Understand the concept of risk, risk assessment and management frameworks; learn to identify, analyze, manage and mitigate and communicate this risk to stakeholders.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1359,'Marshall','BUAD','385x',4,'Introduction to Risk Management and Insurance','Understand the underlying economics, structure, and business models of an organization (often an insurance carrier) willing to accept risk for diverse situations: property, life, healthcare.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1360,'Marshall','BUAD','425',2,'Data Analysis for Decision Making','Leveraging large corporate datasets; slice and dice data; dash boards; data mining and statistical tools; neural network; multiple and logistic regression; decision tree; gain inference and decision making; clustering.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1361,'Marshall','BUAD','490',1,'Directed Research','Individual research and readings under the supervision of a faculty advisor. Application and proposal required. Not available for graduate credit.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1362,'Marshall','BUAD','494',2,'Marshall Honors Research and Thesis','Experience in conducting research and writing a thesis under the supervision of a faculty adviser.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1363,'Marshall','BUAD','495',1,'Practicum in Business Issues (Internship)','Combined classroom discussion and structured, supervised field application of business theories and practices within a part-time employment context. Open only to: Freshmen, Sophomore, Junior, Senior. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1364,'Marshall','BUAD','497',4,'Strategic Management','Examination of managerial decision-making, planning, and policy under changing environments; readings, cases, exercises, simulations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1365,'Marshall','BUCO','333mw',4,'Communication in the Working World - Managing Diversity','Communication strategies to manage workplace diversity. Historical, social, legal precedents. Institutional barriers to diversity. Race, gender, sexual orientation, age, physical disabilities, culture.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1366,'Marshall','BUCO','425',4,'Ethics and Professional Communication','Study the intersection between business and professional leadership, language, and ethics. Analyze and present results to public audiences through publications, professional conferences, ethics case competitions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1367,'Marshall','BUCO','450',2,'Communication for Organizations: Exploring Creativity','Development of individual creative thinking and problem-solving skills; exploration of workplace creativity; advancement of managerial communication skills necessary to foster organizational innovation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1368,'Marshall','BUCO','485',4,'Business Communication Management for Nonprofits','Communication environment; communication activities for fundraising and visibility; research and evaluation methods; grant proposals; strategies for communicating social mission to media, government and for-profit partners.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1369,'Marshall','BUCO','504t',3,'Writing for Accounting and Tax Professionals','Effective written communication for accounting and tax professionals.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1370,'Marshall','BUCO','552',1.5,'Defining and Communicating Your Professional Value','Understand how to define and communicate your individual professional identity and value to employers across diverse professional fields using specialized applied tools.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1371,'Marshall','BUCO','593',0.5,'Independent Research in Management Communication','Independent research beyond normal course offerings. Proposal, research and written report/paper required. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1372,'Marshall','BUCO','633',2,'Writing a Journal Article for Publication','Developing strategies for productive academic writing; drafting and revising an article for journal submission. Open only to doctoral students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1373,'Marshall','BUCO','636',1,'Communication for Doctoral Students: Tutorial','Individualized tutorial focusing on academic writing and presentation skills for dissertations, conference papers, and journal articles.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1374,'Marshall','DSO','401',2,'Business Information Systems -- Spreadsheet Applications','Applied understanding of how spreadsheets are used to analyze business information. Create real world software applications for use in accounting, finance, marketing and operations. Duplicates credit in former IOM-401.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1375,'Marshall','DSO','427',4,'Designing Spreadsheet-Based Business Models','Application of decision analysis, simulation, and optimization techniques to managerial problems. Learn how to create and present useful spreadsheet models to analyze practical business problems. <strong>Recommended preparation:</strong> BUAD-310. Duplicates credit in former IOM-427.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1376,'Marshall','DSO','428',4,'Essentials and Digital Frontiers of Big Data','An overview of key concepts of big data and related digital technologies and their applications to different business problems. Hands-on experience at introductory level.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1377,'Marshall','DSO','431',4,'Foundations of Digital Business Innovation','Foundational frameworks for understanding the planning and execution of digitally-enabled strategic initiatives. Duplicates credit in former IOM-431.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1378,'Marshall','DSO','435',4,'Enterprise Data Architecture','Management of enterprise data architecture including data structures, conceptual data modeling, logical data modeling, structured query language (SQL), and physical optimization of high performance data architecture. Duplicates credit in former IOM 435.',1);
