INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1380,'Marshall','DSO','462',2,'Managing a Small Business on the Internet','Foundational knowledge for managing a small business on the internet including strategies, tools, and resources integrated with hands-on skills for developing a small business website. Duplicates credit in former IOM-462.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1381,'Marshall','DSO','505',1.5,'Sustainable Supply Chains','Sustainability concepts and frameworks, design for environment, closed-loop supply chains, sustainability in sourcing, green facilities, renewable energy, facility location and transportation decisions, strategic sustainability implementation. Web registration open only to graduate business and accounting students. Duplicates credit in the former IOM 505.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1382,'Marshall','DSO','506',1.5,'Sourcing and Supplier Management','Factors to consider when making sourcing decisions (costs, prices, ethics, globalization); Impact of sourcing on other activities such as product design or inventory management. Open only to graduate business students. Duplicates credit in former IOM 506.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1383,'Marshall','DSO','510',1.5,'Business Analytics','Foundational knowledge for business analytics, including strategies, methods, and tools integrated with hands-on skills for defining business analytics for data-driven decision making and innovation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1384,'Marshall','DSO','522',1.5,'Applied Time Series Analysis for Forecasting','Survey of forecasting and time series methods. Models for stationary and nonstationary time series; ARIMA model identification, estimation, and forecast development. Seasonal and dynamic models. Web registration open only to graduate business and accounting students. Duplicates credit in former IOM 522. <strong>Recommended preparation:</strong> GSBA 506ab or GSBA 524 or (GSBA 516 and GSBA 545).',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1385,'Marshall','DSO','528',1.5,'Data Warehousing, Business Intelligence, and Data Mining','Introduction to data-warehousing, multidimensional database, on-line analytical processing, and survey of business intelligence applications that extract useful information from data warehouses. Business applications emphasized.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1386,'Marshall','DSO','530',3,'Applied Modern Statistical Learning Methods','Overview of highly computational modern statistical learning methods; applications of logistic regression, neural networks, LASSO, trees, boosting and GAM, etc., to finance and marketing data.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1387,'Marshall','DSO','531',1.5,'Digital Foundations for Business Innovation','Developing a strategic perspective on emerging digital innovations shaping consumer-oriented businesses. Topics include artificial intelligence, autonomous vehicles, augmented/virtual reality, post-screen usability and cybersecurity.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1388,'Marshall','DSO','545',3,'Statistical Computing and Data Visualization','Data cleaning and reshaping; good vs. bad graphics; univariate, bivariate, trivariate, hypervariate, and time series graphics; interactive graphics; web-related computing. Extensive computer applications using R.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1389,'Marshall','DSO','547',3,'Designing Spreadsheet-Based Business Models','Application of decision analysis, simulation and optimization techniques to managerial problems. Learn how to create and present useful spreadsheet models to analyze practical business models. Online registration open only to graduate business and accounting majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1390,'Marshall','DSO','552',1.5,'SQL Databases for Business Analysts','SQL; relational database systems; data storage; data manipulation; data aggregation.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1391,'Marshall','DSO','553',1.5,'NoSQL Databases in Big Data','NoSQL; semi-structured and unstructured databases; data storage; data manipulation; distributed databases.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1392,'Marshall','DSO','556',3,'Business Models for Digital Platforms','Managing Business models in digital platform ecosystems; designing new products and services for digital platforms; establishing digital platform leadership; assessing emerging niches in digital spaces.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1393,'Marshall','DSO','557a',1.5,'Global Supply Chain Management in International Settings','Study of global logistics infrastructures - water, rail, road, and air - through readings, team research and presentation, international site visits, and final written report. Open only to Global Supply Chain Management majors. Duplicates credit in former IOM 557a.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1394,'Marshall','DSO','557b',1.5,'Global Supply Chain Management in International Settings','The influence of value-added services and regulatory issues on suppliers and supply chains. Readings, team research and presentation, site visits (LA), and final written report. Open only to Global Supply Chain Management majors. Duplicates credit in former IOM 557b.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1395,'Marshall','DSO','566',3,'Marketing Analytics','Applications and models of marketing-related data analyses to the development of data-driven marketing strategies and making data-driven marketing decisions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1396,'Marshall','DSO','570',3,'The Analytics Edge: Data, Models, and Effective Decisions','Decision making under uncertainty using real data applying the most advanced optimization, statistical and probability methods. Duplicates credit in former IOM 570.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1397,'Marshall','DSO','573',3,'Data Analytics Driven Dynamic Strategy and Execution','Advanced applications of data analytics in dynamic strategy formulation and execution; analytics and business methods for data connected enterprises to continuously enhance their competitive advantage.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1398,'Marshall','DSO','580',3,'Project Management','Applications of systems theory and concepts, matrix organizational structures, PERT/CPM project modeling, and management information systems to the management of complex and critical projects. <strong>Recommended preparation:</strong> GSBA 504b or GSBA 534. Open only to business majors. Duplicates credit in former IOM 580.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1399,'Marshall','DSO','581',3,'Supply Chain Management','Issues in supply chain management. Supply chain performance and dynamics. Tools for planning, control and coordination. Supply chain design and strategy. <strong>Recommended preparation:</strong> GSBA 504b or GSBA 534. Duplicates credit in former IOM 581. Open only to business majors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1400,'Marshall','DSO','590',1,'Directed Research','Research leading to the master\'s degree. Maximum units which may be applied to the degree to be determined by the department. Graded CR/NC. Open only to master\'s students. Duplicates credit in former IOM-590.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1401,'Marshall','DSO','592',0.5,'Field Research in Data Sciences or Operations','Individual or team projects studying the practices of an industry, company, government agency, country, geographic region, etc. Proposal, data collection, analyses, and written report. Graded CR/NC. <strong>Recommended preparation:</strong> completion of required M.B.A, M.Acc., or M.B.T. courses. Open only to graduate students. Duplicates credit in former IOM-592.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1402,'Marshall','DSO','593',0.5,'Independent Research in Data Sciences or Operations','Independent research beyond normal course offerings. Proposal, research and written report/paper required. Graded CR/NC. <strong>Recommended preparation:</strong> completion of required M.B.A, M.Acc., or M.B.T. courses. Open only to graduate students. Duplicates credit in former IOM-593.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1403,'Marshall','DSO','595',0.5,'Internship in Data Sciences or Operations','Supervised on-the- job business experience in the student\'s area of interest (Curricular Practical Training). <strong>Recommended preparation:</strong> completion of required MBA, MSGSCM, or MSBUAN courses.',1);
