INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1408,'Marshall','DSO','670',1.5,'Current Research in Operations Management','Critique of the current research-based literature in operations management to include scheduling, forecasting, MRP, technology planning, inventory management, and facilities location and layout. Duplicates credit in former IOM 670.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1409,'Marshall','FBE','206',2,'The Power of Personal Finance','Develop knowledge and skills in areas of money management, budgeting, financial goal attainment, insurance, credit cards, and investments.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1410,'Marshall','FBE','391',4,'Real Estate Finance and Investment','Introduction to income-producing real estate from the perspective of finance, market analysis, capital markets, development and investment. Includes focus on analytical techniques and computer applications.',1);
