INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1412,'Marshall','FBE','403',4,'Introduction to the Legal Environment of Business','Legal principles of business: litigation process, constitutionallaw, torts, product liability, crimes, contracts, sales and leases, intellectual property, international law, agency, employment law, and ethics.',1);
