INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1417,'Marshall','FBE','429',4,'International Business Law','Introductory course on the legal and regulatory environment of international business transactions.',1);
