INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1420,'Marshall','FBE','441',4,'Investments','Theories and applications of investment decision making; behavior of security prices, portfolio theory, asset pricing models, market efficiency, bond valuation and term structure, derivative securities.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1421,'Marshall','FBE','453a',4,'Advanced Practicum in Investment Management','Application of investment management techniques in a laboratory setting. Stock selection, asset allocation, industry analysis, investment thesis research; off-site visits; oral and written presentations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1422,'Marshall','FBE','458',4,'Law, Finance and Ethics','Law and ethics of agency, partnerships, corporations, limited liability companies, governmental regulation, mergers, creditor rights, secured transactions, bankruptcy, securities regulation, and antitrust.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1423,'Marshall','FBE','459',4,'Financial Derivatives','A rigorous introduction to the pricing and corporate use of financial derivatives -- futures, options, forwards, and swaps -- on stocks, exchange rates, bonds, and commodities.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1424,'Marshall','FBE','460',4,'Mergers, Acquisitions and Restructuring','Practical understanding of the major strategic, economic, financial, human resources, and governance issues of mergers, acquisitions, and restructuring.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1425,'Marshall','FBE','462',4,'International Trade, Finance and Commercial Policy','Commercial policies, treaty relationships, examination of policies influencing world trade and finance, the international financial system, exchange rates. Prerequisites: ECON 203 and ECON 205 or ECON 351 and ECON 352. Open only to sophomores, juniors and seniors.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1426,'Marshall','FBE','465',2,'Real Estate Analysis and Computer Modeling','Real estate valuation. Three approaches to valuation: Market comparison, income, and cost. Highest and best use analysis. State of the art real estate software.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1427,'Marshall','FBE','470',4,'Advanced Real Estate Analysis','Mixed lecture/case approach covering market analysis, asset valuation, ownership structure, negotiation, asset management, corporate real estate, portfolio management, and affordable housing, appraisal and advanced financial modelling.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1428,'Marshall','FBE','489',4,'Real Estate Capital Markets','Topics in real estate capital markets including markets for debt and equity; residential and commercial mortgages and mortgage-backed securities; REITs; institutional sources of capital.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1429,'Marshall','FBE','491',2,'Real Estate Finance Colloquium','The roles of economics and finance in real estate. Lessons from history, the origin of real estate bubbles, how real estate markets work.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1430,'Marshall','FBE','506b',1.5,'Quantitative Methods in Finance','The development of multivariate mathematical and statistical methods used in modern finance and economics and applications of these methods.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1431,'Marshall','FBE','523',3,'Venture Capital and Private Equity','Advanced analysis of the economics of the private equity markets. Consider and value private securities in venture capital, initial public offerings, and leveraged buyouts. Prerequisites: GSBA-521b or GSBA-548.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1432,'Marshall','FBE','527',3,'Entrepreneurial Finance: Financial Management for Developing Firms','Internal financial management of developing firms. Cash flow analysis; capital budgeting; sources of financing; risk analysis; measurement of profits; and mergers and acquisitions.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1433,'Marshall','FBE','529',3,'Financial Analysis and Valuation','An applications-oriented course to develop the financial and accounting tools required to do financial planning, valuation and assessment of financial performance.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1434,'Marshall','FBE','531',1.5,'Corporate Financial Policy and Corporate Governance','Advanced analysis of the determinants of corporate capital structure and payout policies, allocation and value of corporate control, and security issuance and retirement.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1435,'Marshall','FBE','532',3,'Corporate Financial Strategy','Linkage between financial theory and policy and corporate strategy; the role of financial managers in developing corporate strategy; applications of concepts and techniques using cases.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1436,'Marshall','FBE','535',1.5,'Applied Finance in Fixed Income Securities','The basic principles underlying fixed income securities and how these principles apply to the practical aspects of fixed income management.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1437,'Marshall','FBE','540',3,'Hedge Funds','Introduction to the investment strategies used by hedge funds, the quantitative tools and business plans used to implement them. Prerequisites: GSBA 521b or GSBA 548. <strong>Recommended preparation:</strong> Statistics and Calculus.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1438,'Marshall','FBE','550',1.5,'High Yield Bond Investing - Managing Credit Risk','Review the structure of the high yield bond market. Develop a practical approach to assess credit risk and understand how deals are structured and priced.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1439,'Marshall','FBE','553a',3,'Applied Portfolio Management','Application of portfolio management techniques in a laboratory setting. Stock selection, asset allocation, industry analysis, investment thesis research; off-site visits; oral and written presentations.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1440,'Marshall','FBE','555',3,'Investment Analysis and Portfolio Management','Analysis and management of common stocks and fixed income securities; development of modern portfolio theory and the efficient market hypothesis; organization of securities markets.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1441,'Marshall','FBE','558',3,'Law for Structuring, Financing, and Managing Businesses','Legal environment of agency law, general and limited partnerships, limited liability companies, corporate formation and financing, corporate governance, securities law, acquisitions, bankruptcy, and business ethics.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1442,'Marshall','FBE','559',3,'Management of Financial Risk','Analysis of commodity, futures, and options contracts; theoretical and empirical approaches; spot and futures price relationships, speculation and hedging strategies; market efficiency.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1443,'Marshall','FBE','571',3,'Introduction to Financial Analysis: Practicum','This course, an introductory financial analysis (tools, techniques) practicum, emphasizes practical application of asset valuation and portfolio management techniques for those with little previous experience. Duplicates credit in FBE 572 and FBE 573. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1444,'Marshall','FBE','589',3,'Mortgages and Mortgage-Backed Securities and Markets','Valuation and analysis of residential and commercial mortgages and mortgage-backed securities and related markets.',1);
