INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1447,'Marshall','FBE','592',0.5,'Field Research in Finance or Business Economics','Individual or team projects studying the business practices of an industry, company, government agency, country, geographic region, etc. Proposal, data collection, analyses, and written report. Graded CR/NC.',1);
