INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1452,'Marshall','FBE','631a',1.5,'Advanced Corporate Finance','Advanced and frontier issues in corporate finance. A: financial intermediation, investment-cash flow sensitivity, internal capital markets, diversification, and business groups.',1);
