INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1456,'Marshall','GSBA','520x',3,'Business Fundamentals for Non-Business Professionals','An overview of concepts, tools and principles of business management to develop a general management point of view. Open only to non-business graduate students.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1457,'Marshall','GSBA','523t',3,'Communication for Accounting and Tax Professionals','Communication strategies to ensure effective communication to internal and external business audiences at all levels; business writing and presentations; electronic communication; communicating ethics in business. (Duplicates credit in GSBA 502, the former GSBA 502b, GSBA 523, and GSBA 542)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1458,'Marshall','GSBA','528',3,'Marketing Management','Marketing is treated as a managerial decision-making process. Emphasis is given to understanding the concepts, tools, and techniques that comprise a comprehensive marketing strategy. Duplicates credit in GSBA 509 and GSBA 509ab.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1459,'Marshall','GSBA','534',2,'Operations Management','Formulation, modeling, analysis, and optimization of business decision problems; survey of concepts and techniques necessary to manage the operations function of the firm. <strong>Recommended preparation:</strong> GSBA 506b or GSBA 524 or GSBA 516 and GSBA 545. (Duplicates credit in GSBA-504ab)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1460,'Marshall','GSBA','542',1.5,'Communication for Management','Internal and external communication, research methods, reports for decision-making, oral presentations and briefing, strategies to assure communication; field studies. Duplicates credit in GSBA 502, GSBA 523, GSBA-523T, and the former GSBA-502ab.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1461,'Marshall','GSBA','545',1.5,'Data Driven Decision Making','Data analysis technologies for business decision making; principles and techniques of statistical inference for business problem solving; foundations of data-driven regression and time series analytics. Duplicates credit in GSBA 506ab and GSBA 524.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1462,'Marshall','GSBA','548',2,'Corporate Finance','Modern theory of corporate investment and financing decisions. Online registration open only to Business and Accounting majors. (Duplicates credit in GSBA 521a and GSBA 521b.)',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1463,'Marshall','GSBA','595',0.5,'Internship in Business','Supervised on-the-job business experience in the student\'s area of interest. (Curricular Practical Training) Graduate business students only. Graded CR/NC.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1464,'Marshall','GSBA','602',3,'Selected Issues in Economic Theory I','Methodology and research perspectives of economics; contribution of the economics paradigm to accounting, management, finance, marketing, and decision sciences. <strong>Recommended preparation:</strong> Admission to doctoral program in business administration or departmental approval.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1465,'Marshall','GSBA','604',3,'Regression and Generalized Linear Models for Business Applications','Theory and application of linear regression models; role of substantive theory in statistical model building; model specification, estimation, diagnostic checking; the general linear hypothesis.',1);
INSERT INTO `course` (`ID`,`school`,`major`,`number`,`units`,`name`,`description`,`semester`) VALUES (1466,'Marshall','GSBA','625',3,'Designing and Running Experiments','Introduction to design and implementation of experiments. Single and multiple factors, fully crossed and fractional factorial designs, repeated measures, measurement, manipulations, subject choice, demand effects. Open on to Ph.D. students.',1);
